INSERT INTO `qb_zdasjqbxx` VALUES (1640, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'bq', '2018-08-28 10:30:09', '8月28日17名光辉、城商投资平台投资人因资金被骗到省政府上访', '     08月28日09时40分许，光辉民融登平台（地址在官渡区银海领域小区内）15人、城商互联网金融平台2人投资人到省政府上访，反映，他们3年前投资此两平台，后资金无法拿到，发现被骗，遂向各级政府相关部门反映、报案，但至今没有结果，来访人称他们了解到儒商、泛亚民融登等其他类似的15个资金被骗的平台报案后均已立案，只有他们两家至今没有立案，故到省政府上访，投诉金融办、公安部门的不作为行为，要求上级政府督促公安机关尽快立案，追赃挽损。省信访局接待后告知其向昆明市政府反映。10时05分许，来访人全部离开省政府。（华山所）\r\n\r\n\r\n\r\n\r\n\r\n\r\n', '2018-08-28 09:40:00', '2018-08-28 10:05:00', 0.42, 17, '民间借贷 ', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-08-29 09:06:18', '2018-08-29 09:25:18', NULL, '投资户', NULL, '0', '1', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2505, 'cz', '城镇', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'tsqt', '特殊群体', 'jzwt', '集资问题', 'sldws', '16-50人', '1', 'qkfy', 17, 0.00, 0, 0, '0', NULL, '情况反映', 'b834d5db-7122-44a7-884d-b79fdf2b2eb7', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
