INSERT INTO `qb_zdasjqbxx` VALUES (1747, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2018-09-15 12:14:31', '实力壹方城小区车库出口被堵', '2018年9月15日9时30分许因停车收费问题，实力一方城30余名业主驾驶6、7辆车将小区进出车库的通道全部堵死, 分局已经派出警力到现场进行处置，住户情绪暂时稳定。小区物业管理负责人常志军（男，37岁）称，其停车费收费标准是根据昆发改价（2017）92号文件制定的，并在1个月前进行了张贴公示，费用为30分钟起收费4元人民币，之后每小时加收2元人民币，每日单次20元人民币封顶。相关情况密切掌握中。\r\n', '2018-09-15 09:30:00', '2018-09-15 11:00:00', 1.50, 10, '物业管理', '马村所', '实力壹方城小区', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-09-17 09:27:32', '2018-09-20 09:54:28', NULL, '小区业主', NULL, '1', '1', '堵路', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '实力壹方城小区', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2742, NULL, NULL, NULL, 'gzfy', '工作反映', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', 'qkfy', 0, 0.00, 0, 0, '1', NULL, '情况反映', 'ccc26f8d-02e0-4d66-a289-519a4a40247d', 'JS', NULL, 'Y', 110, NULL, NULL, '530102540000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
