INSERT INTO `qb_zdasjqbxx` VALUES (1753, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'bq', '2018-09-18 09:44:03', '景洪市曼典村村民5人因不满征地补偿款一事到省政府上访', '18日08:40分许，5名景洪市嘎洒镇曼典村村民到省政府上访，反映，2011年西双版纳景洪工业园区二期开发项目进行征地，该村村委会领导在未召开村民大会的情况下，私自与工业园区签订征地补偿协议，将该村68亩水田以每亩4.5万元的价格卖给工业园，村民称，他们了解到2010年景洪市国土资源局同样征收了相同性质的水田，征地补偿价是每亩25万元，此次征地的补偿价过低，引发该村100余名村民不满，多次向有关部门反映要求一视同仁给予补偿未得到答复所以到省政府上访，要求按照每亩25万元给予村民补偿。省信访局接待后告知其回景洪市政府反映。09:10分许，5人全部离开省政府。\r\n\r\n\r\n\r\n\r\n\r\n华山东路派出所\r\n\r\n2018 年9月18日\r\n', '2018-09-18 08:40:00', '2018-09-18 09:10:00', 0.50, 5, '征地补偿', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-09-19 09:13:31', '2018-09-19 09:20:07', NULL, '村民', NULL, '0', '1', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2507, 'nn', '农村', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', 'zd', '征地', 'sryx', '10人以下', '1', 'qkfy', 5, 0.00, 0, 0, '0', NULL, '情况反映', 'fa4bf459-8b7b-47af-a390-40e5304e5982', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
