INSERT INTO `qb_zdasjqbxx` VALUES (1758, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'bq', '2018-09-19 15:35:26', '在官渡区做工的农民工5人因工钱被拖欠到省政府上访', '19日14:20分许，5名农民工到省政府上访，反映，他们8人受雇于建丰建筑公司，于2017年5月起，在官渡区俊发地产云秀路与环湖东路交叉路口的滇池华府10号地块项目部做工，当年年底工程完工，期间8人拿到一部分工钱，但建丰公司至今仍然拖欠8人工钱共计39800元一直未付。由于讨要未果所以到省政府上访，要求尽快拿到工钱。省信访局接待后告知其向省劳动监察局反映。15：05分许，5人全部离开省政府。\r\n', '2018-09-19 14:20:00', '2018-09-19 15:05:00', 0.75, 5, '索薪讨款', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-09-20 09:51:01', '2018-09-20 10:03:15', NULL, '农民工', NULL, '0', '1', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2519, 'cz', '城镇', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', 'lzjf', '劳资纠纷', 'sryx', '10人以下', '1', 'qkfy', 5, 0.00, 0, 0, '0', NULL, '情况反映', 'da4a13b7-a0f2-462a-bbaa-9bc7a78db377', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (1759, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'bq', '2018-09-19 15:32:55', '高木修公司7名工作人员聚集莲花池畔小区金鼎盛房地产公司讨要装修工程款', '9月19日11时许，昆明高木装饰公司7名工作人员聚集莲花池畔小区金鼎盛房地产公司讨要装修工程款，并声称将邀约100余名工人前来讨要。据了解，高木装饰公司于2012年10月承包金鼎盛房地产公司名下莲花池畔小区9栋楼房的装修装饰工程，2014年5月竣工后，金鼎盛房地产开发公司尚拖欠1800万工程款未支付，致公司无力支付工人薪资。经分局、区劳动监察大队工作，双方约定20日到区住建局协商。相关情况进一步掌握中。（莲华所）\r\n\r\n\r\n\r\n\r\n\r\n\r\n', '2018-09-19 11:00:00', '2018-09-19 11:30:00', 0.50, 7, '索薪讨款', '莲华所', '金鼎盛房地产公司', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-09-20 09:51:01', '2018-09-20 10:02:29', NULL, '农民工', NULL, '0', '1', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', '金鼎盛房地产公司', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2636, 2519, NULL, NULL, 'fyqk,mjjf', '反映情况,民间纠纷', NULL, NULL, 'qy', '请愿', 'gtgsyz', '个体工商业者', NULL, NULL, 'sryx', '10人以下', '1', 'qkfy', 7, 0.00, 0, 0, '0', NULL, '情况反映', '84ad605c-b164-401d-a3a7-11f79ec8d8ad', 'JS', NULL, 'Y', 110, NULL, NULL, '530102420000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (1760, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'bq', '2018-09-19 11:19:50', '9月19日10名汽车厂退休职工聚集在东风云南汽车厂门口', '9月19日9时04分许，10余名东风云南汽车厂的退休职工分散聚集在云南汽车厂大门两侧人行道上，要求东风云南汽车厂解决他们的问题及诉求，在下厂区大门口侧边栏杆处、门上分别横挂4幅内容相同的白底黑字布标，内容为“要求公开土地置换合同、严查国有土地流失和国有资产流失”。“严惩贪污腐败，我们要吃饭、我们要生存、还我血汗钱。”汽车厂职工分散聚集在厂区门口和两旁人行道上，无过激行为。至10时08分许聚集职工自行收拾标语离开，未出现堵路及影响交通的情况。相关情况持续关注中。（黑林铺所）\r\n\r\n\r\n\r\n \r\n\r\n\r\n', '2018-09-19 09:04:00', '2018-09-19 10:08:00', 1.07, 10, '身份待遇', '黑林铺所', '东风云南汽车厂', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-09-20 09:51:01', '2018-09-20 09:59:56', NULL, '退休职工', NULL, '0', '1', '拉横幅', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '东风云南汽车厂', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2606, 2556, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', '2ab886fa-cc1a-48f7-bc82-ef7fb8c8066f', 'JS', NULL, 'Y', 110, NULL, NULL, '530102570000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
