INSERT INTO `qb_zdasjqbxx` VALUES (1767, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'bq', '2018-09-20 10:10:23', '9月20日云南省文山州砚山县维摩乡炭房社区金平组25民村民到省信访局上访', '9月20日9时许，云南省文山州砚山县维摩乡炭房社区金平组25民村民到省信访局上访，反映，自2009年5月维摩乡阿坡塘山心丫口采石场长期开山炸石致使村民房屋震动开裂，多次协商赔偿无果，后于2017年1月17日经砚山县法院组织双方调解。但在调解过程中，村民一方认为法院调解不公，多次向上级法院反映无果，现到省信访局反映督促法院公平调解。（华山所）\r\n', '2018-09-20 09:00:00', '2018-09-20 10:00:00', 1.00, 25, '损害赔偿 ', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-09-21 10:32:11', '2018-09-21 10:34:35', NULL, '村民', NULL, '0', '1', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2507, 'dzjg', '党政机关', 'fyqk,mjjf,qtxsj', '反映情况,民间纠纷,群体性事件', NULL, NULL, 'sf', '上访', 'nm', '农民', 'glsg', '各类事故', 'sldws', '16-50人', '1', 'qkfy', 25, 0.00, 0, 0, '0', NULL, '情况反映', '82e2a39a-486a-42de-a170-26efe9cf83fb', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
