INSERT INTO `qb_zdasjqbxx` VALUES (1769, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'bq', '2018-09-21 16:49:17', '昆明风行防水材料有限公司16名员工到西山大厦北京建工工地讨要材料款', '9月21日10时许，昆明风行防水材料有限公司杨俊峰（31岁）等15人聚集西山大厦北京建工工地，向北京建工讨要拖欠的23余元材料款。经工作，北京建工称12月份才能付清欠款。杨称将不定期前来讨要材料款，于15时许自行离开。现不排除欠款纠纷升级扩大可能。相关情况进一步关注中。\r\n\r\n\r\n\r\n\r\n', '2018-09-21 10:00:00', '2018-09-21 12:00:00', 2.00, 15, '索薪讨款', '黑林铺所', '北京建工工地', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-09-25 10:03:20', '2018-09-25 10:13:07', NULL, '农民工', NULL, '0', '1', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '北京建工工地', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2771, 2519, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', 'e4929f5b-4fe3-475b-b4f9-e1cb392d63d6', 'JS', NULL, 'Y', 110, NULL, NULL, '530102570000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (1770, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'bq', '2018-09-21 11:22:52', '9月21日15名汽车厂退休职工聚集在东风云南汽车厂门口', '9月21日9时25分许，15名东风云南汽车厂的退休职工分散聚集在云南汽车厂大门两侧人行道上，要求东风云南汽车厂解决他们的问题及诉求，在下厂区大门口侧边栏杆处、门上分别横挂4幅内容相同的白底黑字布标，内容为“要求公开土地置换合同、严查国有土地流失和国有资产流失”。“严惩贪污腐败，我们要吃饭、我们要生存、还我血汗钱。”汽车厂职工分散聚集在厂区门口和两旁人行道上，无过激行为。至10时12分许聚集职工自行收拾标语离开，未出现堵路及影响交通的情况。相关情况持续关注中。（黑林铺所）\r\n\r\n\r\n\r\n \r\n\r\n\r\n', '2018-09-21 09:25:00', '2018-09-21 10:12:00', 0.78, 15, '身份待遇', '黑林铺所', '东风云南汽车厂', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-09-25 10:03:20', '2018-09-25 10:10:03', NULL, '退休职工', NULL, '0', '1', '拉横幅', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '东风云南汽车厂', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2606, 2556, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', '10c1fe7c-122a-46fe-bf35-2f6c9c14c0f1', 'JS', NULL, 'Y', 110, NULL, NULL, '530102570000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
