INSERT INTO `qb_zdasjqbxx` VALUES (1774, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'bq', '2018-09-22 17:18:32', '30名12.13人员到崇仁街原泛亚总部聚集（续报）', '2018年9月22日13时45分许，陆续有30名（女性20名、男性10名）12.13人员到崇仁街原泛亚总部聚集。\r\n\r\n据民警掌握，现场人员共打印了300份《共同聘请法律顾问授权委托书》，至2018年9月22日16时38分许有180余人到现场填写《共同聘请法律顾问授权委托书》，其中150余人在填完《共同聘请法律顾问授权委托书》后就离开现场，其余30人在现场聊天，现场无人交纳参与司法维权的费用，止2018年9月22日16时38分许，所有人员已离开原泛亚总部，现场秩序平稳，无异常情况。相关情况进一步掌握中。\r\n\r\n\r\n', '2018-09-22 13:45:00', '2018-09-22 16:38:00', 2.88, 180, '涉众型经济案件', '国保大队', '泛亚总部', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-09-25 10:04:11', '2018-09-25 10:14:42', NULL, '12.13', NULL, '0', '2', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', '泛亚总部', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2501, NULL, NULL, NULL, NULL, NULL, NULL, 'zzhd', '组织活动', NULL, NULL, NULL, NULL, 'yblyr', '101-200人', '2', 'qkfy', 180, 0.00, 0, 0, '0', NULL, '情况反映', '690c8ced-4d49-41b6-8e4a-0b370a3b224e', 'JS', NULL, 'Y', 110, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
