INSERT INTO `qb_zdasjqbxx` VALUES (1777, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'bq', '2018-09-24 09:43:52', '百余名保利大家业主因质疑物管公司不作为发生聚集', '2018年9月23日晚22时29分，因保利大家业主禄劝籍男子李建明（29岁，住保利大家小区14栋2单元2303室）醉酒将保利大家小区路边停放的4辆轿车不同程度损坏，引发小区业主质疑保利大家物业不负责任，认为物业公司只收取停车费，不保护业主财物安全等问题。随后100余名业主到小区物业办公室外呼喊口号，要求物业方出面说明。王家桥派出所接报后安排民警现场维护秩序，开展法制宣传及劝解工作，现场业主无其余过激行为。后王家派出所将李建明带回派出进行调查取证，聚集业主于24日0时40分许陆续散离。（王家桥所）\r\n', '2018-09-24 22:29:00', '2018-09-24 00:40:00', 21.82, 100, '损害赔偿 ', '王家桥所', '保利大家', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-09-25 10:06:11', '2018-09-25 10:34:35', NULL, '小区业主', NULL, '0', '1', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '保利大家', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2613, NULL, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', '9656c57e-4f77-41fd-9af7-0f35b87f5cc9', 'JS', NULL, 'Y', 110, NULL, NULL, '530102580000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
