INSERT INTO `qb_zdasjqbxx` VALUES (1780, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'bq', '2018-09-25 16:30:06', '9月25日金座90余人到西站法庭听取法院宣判', '09月25日14时许，90余名金座公司非法集资案受害群众陆续到西站法庭听取法院宣判。\r\n\r\n昆明市五华区人民法院判决：“由被告李媛芬于判决生效之日起十日内返还原告杨庆馨、罗永全16301元。”到场群众对判决结果不满，商议于9月26日上午9时到昆明市体育馆聚集，后到五华区人民法院、昆明市中级人员法院讨要说法。\r\n\r\n截止目前，现场剩余6名群众，其余群众已陆续离开，未见异常，民警继续关注事件动态，如有情况将及时上报。（翠湖所）\r\n', '2018-09-25 14:00:00', '2018-09-25 16:00:00', 2.00, 90, '特殊群体', '翠湖所', '西站法庭', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-09-26 15:32:07', '2018-09-26 15:34:50', NULL, '金座', NULL, '0', '2', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '西站法庭', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2645, 2502, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', 'f4006139-a7b5-445e-a6f6-a11623201778', 'JS', NULL, 'Y', 110, NULL, NULL, '530102430000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (1781, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'bq', '2018-09-25 15:10:19', '9月25日90余名金座集资人员到市体育馆上访', '<p>2018年9月25日9时10分许， 90余名金座人员到市体育馆进行例行上访。市中级人民法院相关工作人员在现场进行接访，民警正在现场开展秩序维护疏导化解等工作。上访人员秩序良好，无过激行为。至14时15分许，全部上访金座人员自行离开市体育馆。（茭菱所）</p>\n', '2018-09-25 09:10:00', '2018-09-25 14:15:00', 5.08, 110, '特殊群体', '五华分局', '市体育馆', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-09-26 15:32:07', '2018-11-12 14:21:59', NULL, '金座', NULL, NULL, NULL, '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2428, 2502, NULL, NULL, NULL, NULL, NULL, NULL, 'sf', '上访', NULL, NULL, NULL, NULL, 'yblyr', '101-200人', '2', NULL, NULL, 0.00, 0, 0, NULL, NULL, NULL, '13564b85-8e35-489f-ae36-2f150c92b8f9', 'JS', NULL, 'Y', 110, NULL, NULL, '530102000000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (1782, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2018-09-26 14:05:53', '9月26日宣威市水务局职工代表4人因不满工资被拖欠到省政府上访', '26日10：10分许，4名宣威市水务局职工到省政府上访，反映他们是宣威市水务局灌区管理中心下属的偏桥、新屯、羊过水、东屯、石城河5座中型水库的管理人员，职工。2005年当地水务系统管理体制改革时，由于改革不彻底，长期出现职工管理经费、公用经费等日常维护经费未落实的情况，致使灌区管理中心水库管理人员经费和工资等无保障，特别是2017年下半年至今已多次拖欠工资，住房公积金及医保等均未发放，引发他们不满，涉及51人。因多次向当地政府讨要工资未果所以到省政府上访，要求尽快解决工资等问题。\r\n\r\n来访人员称,今日他们有40余人准备到访，有4人作为代表进入接访大厅反映诉求，其余人员在外等待。省信访局接待后告知其向曲靖市政府反映后。10:40分许，40余人全部离开省政府。（华山所）\r\n\r\n\r\n', '2018-09-26 10:10:00', '2018-09-26 10:40:00', 0.50, 4, '索薪讨款', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-09-27 09:12:35', '2018-09-27 09:17:19', NULL, '企事业单位职工', NULL, '0', '1', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2537, 'cz', '城镇', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'xgsyry', '下岗失业人员', 'gzfldy', '工资福利待遇', 'sryx', '10人以下', '1', 'qkfy', 4, 0.00, 0, 0, '0', NULL, '情况反映', '4b172e26-8024-4ffd-bb32-05ca4103c916', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (1783, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'bq', '2018-09-26 15:55:05', '9月26日金座群体维权情况', '9月26日9时许，因对“陶炳芳（金座集资户）等人因维权活动经费问题，诉李媛芬不当得利纠纷诉”一案的判决结果不满，陆续有近90余名以纪昌凤、罗益民为首的金座集资户到环城西路的五华区法院原址后，因五华区法院已搬至普吉路五华区行政司法中心，故其中80余名集资户陆续分乘公交车，继续到五华区行政司法中心的五华区法院聚集，经法院工作人员做工作，12时49分，聚集的集资户离开。26日14时许，该群体选派了李媛芬等8名集资户到市中院上访，15时30分许离开。\r\n\r\n另据个掌握，以纪昌凤、罗益民为首的金座集资户拟于9月27日9时，继续到市体育馆上访，要求市金融办的领导接访，预计参加人数在100人左右。\r\n\r\n相关情况在进一步掌握中。（国保大队）\r\n\r\n\r\n', '2018-09-26 09:00:00', '2018-09-26 15:30:00', 6.50, 90, '特殊群体', '国保大队', '五华区法院', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-09-27 09:12:35', '2018-09-27 09:19:07', NULL, '金座', NULL, '0', '2', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '五华区法院', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2604, 2502, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', '6590593e-94ee-40b1-b0c9-029a74e1fc82', 'JS', NULL, 'Y', 110, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (1784, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'bq', '2018-09-26 16:54:27', '9月26日部分“12.13”人员到崇仁街泛亚总部聚集', '9月26日14时许，陆续有侬丽、李艳红等20余名在昆“12.13”人员到我市崇仁街泛亚总部门前聚集，聚集过程中，侬丽、李艳红等人组织到现场的群体成员进行登记签名，填写了《共同聘请法律顾问委托书》，并向李艳红缴纳了司法维权的费用。16时10分左右，上述人员陆续离开现场。\r\n\r\n相关情况在进一步掌握中。（国保大队）\r\n\r\n\r\n', '2018-09-26 14:00:00', '2018-09-26 16:10:00', 2.17, 20, '涉众型经济案件', '国保大队', '泛亚总部', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-09-27 09:12:35', '2018-09-27 09:23:15', NULL, '12.13', NULL, '0', '2', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '泛亚总部', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2501, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', '80069c73-c342-4cea-a317-5270721d962b', 'JS', NULL, 'Y', 110, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (1785, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2018-09-26 16:38:18', '9月26日全省统招未分配大中专毕业生省政府群访情况（三）', ' 11时30分许，现场群访人员总数达260余人，据初步掌握，玉溪地区128人，昭通84人，丽江20余人，昆明15人，大理10余人，另外还有少数楚雄、普洱等地人员。省信访局已安排各地州工作组分别开展劝返稳控工作。截至上报时，现场聚集的上访人员有半数自行离开到周边午饭，现场滞留人员秩序平稳，无人员离开迹象，各劝返组工作暂无进展。分局现场警力开展工作中。至16时20分许，接待结束，告知其今年底前各地州政府会给予他们一个明确的答复，要求其耐心等待。现代表及各地工作组正在对4地到访人员开展劝返工作，目前还有大约50余名上访人员未离开。密切关注中。（华山所）\r\n\r\n\r\n\r\n\r\n\r\n\r\n', '2018-09-26 09:00:00', '2018-09-26 18:40:00', 9.67, 260, '身份待遇', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-09-27 09:12:35', '2018-09-27 09:22:13', NULL, '大中专毕业生', NULL, '0', '2', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2542, 'cz,nn', '城镇,农村', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'tsqt', '特殊群体', 'dzzbm', '对政策不满', 'eblyr', '201-500人', '2', 'qkfy', 260, 0.00, 0, 0, '0', NULL, '情况反映', '36fc0d48-b00f-431d-8067-076b76cd67ce', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (1786, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'bq', '2018-09-26 10:44:48', '9月26日10名东风云南汽车厂退休职工厂门聚集', '\r\n\r\n9月26日9时07分许，10名东风云南汽车厂的退休职工分散聚集在厂大门两侧人行道上，要求东风云南汽车厂解决他们的问题及诉求，在厂区大门口侧边栏杆处、门上分别横挂4幅内容相同的白底黑字布标，内容为“要求公开土地置换合同、严查国有土地流失和国有资产流失”。“严惩贪污腐败，我们要吃饭、我们要生存、还我血汗钱。”\r\n\r\n职工分散聚集,无过激行为。至10时20分许全部自行收拾标语离开，过程平稳。（黑林铺所）\r\n', '2018-09-26 09:07:00', '2018-09-26 10:20:00', 1.22, 10, '身份待遇', '黑林铺所', '东风云南汽车厂', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-09-27 09:12:35', '2018-09-27 09:15:54', NULL, '退休职工', NULL, '0', '1', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '东风云南汽车厂', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2606, 2556, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', '6f86677e-d705-40ad-9eac-49b703e1e443', 'JS', NULL, 'Y', 110, NULL, NULL, '530102570000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (1787, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'bq', '2018-09-26 10:35:35', '9月25日40余名保利大家业主因停车问题再次聚集', '9月25日21时40分许，王家桥所在保利大家小区的治安志愿者通报，有40余名业主在小区进出口与物业公司人员对峙，主要原因是业主要求把车开进去小区道路停放遭到物业拒绝。\r\n\r\n王家桥所值班领导立即带警力到场开展工作，据了解：现场物业公司余人组成人墙，阻住业主及车辆进入小区绿化道路停，聚集业主情绪激动，后一名业主驾摩托车准备强行冲入，后摩托车被推翻，驾车业主摔倒，造成皮外伤，物业公司2人受伤。民警立即将业主与物业人员隔开，将涉事受伤人员交120送往医院治疗，对业主开展劝解，之后业主情绪缓和，陆续离开现场。0时许，经民警工作，业主全部离开现场。\r\n\r\n因停车费用问题，业主与小区物管的矛盾已逐步加深，业主也反复寻找事端与物管人员发生摩擦，下一步，极有可能因小摩擦引发重大治安案件，建议高度关注。（王家桥所）\r\n\r\n                  \r\n\r\n\r\n\r\n\r\n', '2018-09-25 21:40:00', '2018-09-25 11:00:00', 10.67, 40, '物业管理', '王家桥所', '保利大家', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-09-27 09:12:35', '2018-09-27 09:14:50', NULL, '小区业主', NULL, '0', '2', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '保利大家', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2613, NULL, NULL, NULL, 'gzfy', '工作反映', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', 'a5e6eeb3-539c-4e90-912d-a2dee1c42297', 'JS', NULL, 'Y', 110, NULL, NULL, '530102580000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (1788, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'bq', NULL, '云南新源再生产业有限公司10名退休职工在新源阁酒店聚集', '9月26日，云南新源再生产业有限公司10名退休职工仍旧在新源阁酒店聚集，要求诺嘉公司归还新源酒店的经营权，退休员工分布在酒店二楼房间内看电视、聊天等，我所民警已对在场人员进行政策宣讲，无过激行为。截止情况上报前6名退休职工在新源酒店留宿，相关情况黑林铺所持续关注中。', '2018-09-26 09:00:00', '2018-09-26 00:00:00', 9.00, 10, '合同纠纷 ', '黑林铺所', '新源阁酒店', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-09-27 09:42:43', NULL, NULL, '退休职工', NULL, NULL, NULL, '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, NULL, NULL, NULL, '区内', '新源阁酒店', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2758, 2556, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'JS', NULL, 'Y', 110, NULL, NULL, '530102570000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
