INSERT INTO `qb_zdasjqbxx` VALUES (1796, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'bq', '2018-09-27 14:17:42', '20名东风云南汽车厂退休职工聚集在滇缅大道东风云南汽车厂门口', '9月27日9时5分许，20名东风云南汽车厂的退休职工分散聚集在云南汽车厂大门两侧人行道上，要求东风云南汽车厂解决他们的问题及诉求，在下厂区大门口侧边栏杆处、门上分别横挂4幅内容相同的白底黑字布标，内容为“要求公开土地置换合同、严查国有土地流失和国有资产流失”。“严惩贪污腐败，我们要吃饭、我们要生存、还我血汗钱。”汽车厂职工分散聚集在厂区门口和两旁人行道上，无过激行为。至10时15分许聚集职工自行收拾标语离开，未出现堵路及影响交通的情况。相关情况持续关注中。\r\n\r\n\r\n', '2018-09-27 09:05:00', '2018-09-27 10:15:00', 1.17, 20, '身份待遇', '黑林铺所', '东风云南汽车厂', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-09-28 09:14:11', '2018-09-28 09:15:21', NULL, '退休职工', NULL, '0', '1', '拉横幅', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '东风云南汽车厂', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2606, 2556, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', '554062ec-e9bd-424c-b655-72c10bb05a93', 'JS', NULL, 'Y', 110, NULL, NULL, '530102570000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
