INSERT INTO `qb_zdasjqbxx` VALUES (5007, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'bq', '2018-10-17 11:34:27', '10月17日10余名东风云南汽车厂退休职工聚集在汽车厂门口', '<p><span style=\"font-size:22px\">10月17日9时03分许，10余名东风云南汽车厂的退休职工分散聚集在云南汽车厂大门两侧人行道上，要求东风云南汽车厂解决他们的问题及诉求，在下厂区大门口侧边栏杆处、门上分别横挂4幅内容相同的白底黑字布标，内容为&ldquo;要求公开土地置换合同、严查国有土地流失和国有资产流失&rdquo;。&ldquo;严惩贪污腐败，我们要吃饭、我们要生存、还我血汗钱。&rdquo;汽车厂职工分散聚集在厂区门口和两旁人行道上，无过激行为。至10时34分许聚集职工自行收拾标语离开，未出现堵路及影响交通的情况。相关情况持续关注中。（黑林铺所）</span></p>\n', '2018-10-17 09:03:00', '2018-10-17 10:34:00', 1.52, 10, '身份待遇', '黑林铺派出所', '东风云南汽车厂', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-10-18 09:37:29', '2018-10-31 10:53:30', NULL, '退休职工', 'qkfy', NULL, NULL, '拉横幅', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 110, NULL, 2606, 2556, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', NULL, NULL, 0.00, 0, 0, 'yb', NULL, NULL, 'ddd40be2-c0ff-46ba-ac5d-d83dd3bb4706', 'JS', NULL, 'Y', 110, 151951, 'zzwd', '530102570000', '吴兰军', '吴文', NULL, '吴文', 5006, 'ZQYSB', 'N', NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (5009, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'bq', '2018-10-17 10:27:28', '10月17日碧鸡名城二期业主32人因不满延期交房到省政府上访', '<p><span style=\"font-size:24px\">17日09时55分许，32名碧鸡名城二期业主到省政府上访，反映，他们从2014年起陆续购买了位于西山区碧鸡名城二期的商品房，当时合同约定2016年9月30日交房，但至今二期的房子一直是烂尾楼，且交房时间已过去两年，目前仍然没有动工修建的迹象，引发300余名购房者不满，他们多次向政府有关部门反映要求尽快交房未果所以到省政府上访，要求：1：尽快将建好并验收合格的房子交付业主；2、赔偿业主违约金，解决业主入住前的租房、贷款等生活问题；3、请求政府查明碧鸡公司的财务状况等资金流动问题。现信访接待中。（华山所）</span></p>\n', '2018-10-17 09:55:00', '2018-10-17 11:00:00', 1.08, 32, '产权物业', '华山东路派出所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-10-18 09:37:29', '2018-10-31 10:54:28', NULL, '业主购房者', 'qkfy', NULL, NULL, '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 110, NULL, 2392, 2528, 'cz', '城镇', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'tsqt', '特殊群体', 'mjjf', '民间纠纷', 'sldws', '16-50人', '2', NULL, NULL, 0.00, 0, 0, 'yb', NULL, NULL, '9529f85d-ab18-4394-9a36-727c1b27b007', 'JS', NULL, 'Y', 110, 151951, 'zzwd', '530102450000', '李敏', '吴文', NULL, '吴文', 5008, 'ZQYSB', 'N', NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (5011, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'bq', '2018-10-17 10:00:48', '10月16日部分金座集资户到市体育馆上访的情况', '<p><span style=\"font-size:24px\">据工作掌握：2018年10月16日9时以罗益民、季昌凤为首的金座集资户到市体育馆上访，昆明市金融办、昆明市五华、西山、盘龙、官渡区金融办的相关领导进行了接访，要求四个区的金融办给予困难补助。参加人数在90人左右，12时30分，上述人员离开现场。 另据工作掌握，2018年10月17日是重阳节，金座群体拟在小西门旁边的园林餐厅，举行聚餐活动，预计参加人数在90人左右。 民警将会密切关注金座群体的相关动态，并继续配合政府职能部门做好该群体的疏导化解和重点人的管控工作。&nbsp;&nbsp; 相关情况在进一步了解掌握中。 （国保大队）</span></p>\n', '2018-10-16 09:00:00', '2018-10-16 12:30:00', 3.50, 90, '特殊群体', '茭菱派出所', '市体育馆', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-10-18 09:37:30', '2018-10-19 10:22:36', NULL, '金座', 'yj', NULL, NULL, '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 110, NULL, 2428, 2502, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', NULL, NULL, 0.00, 0, 0, 'yb', NULL, NULL, '55f36abf-5e5a-4ea1-b864-0e867042fd04', 'JS', NULL, 'Y', 110, 151951, 'zzwd', '530102550000', '李志洪', '吴文', NULL, '吴文', 5010, 'ZQYSB', 'N', NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (5013, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'bq', '2018-10-18 14:50:27', '10月18日90余名金座集资户例行到市体育馆聚集', '<p><span style=\"font-size:24px\">9月18日9时许，陆续有90余名金座集资户到市体育馆聚集，五华区等政府部门的工作人员到现场，并准备了近百份米和油向聚集的集资户发放，因部分聚集的集资户认为，米和油的价值仅在100元左右，与之前政府承诺的发放价值150元左右的米和油有较大差距，故拒绝领取，现五华区等政府部门的工作人员正在现场开展疏导化解工作。相关情况在进一步掌握中。</span></p>\n', '2018-10-18 09:00:00', '2018-10-18 11:00:00', 2.00, 90, '特殊群体', '茭菱派出所', '市体育馆', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-10-19 09:57:29', '2018-10-31 10:58:19', NULL, '金座', 'qkfy', NULL, NULL, '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 110, NULL, 2428, 2502, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', NULL, NULL, 0.00, 0, 0, 'yb', NULL, NULL, '9a43c18b-133c-4f42-823f-5071b3bab79c', 'JS', NULL, 'Y', 110, 151951, 'zzwd', '530102550000', '徐治强', '李向宇', NULL, '李向宇', 5012, 'ZQYSB', 'N', NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (5017, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'bse', '2018-10-18 14:00:55', '省发改委25名军转干部因身份问题到省政府上访', '<p><span style=\"font-size:20px\">18日11时20分许，省发改委25名军转干部因身份问题到省政府上访。来访人员称，他们了解到，2011年，为解决军转干部的安置问题，发改委主要领导召开了一次党委会，研究决定从当年起，凡进入发改委的军转干部不再招录为公务员，一律安置到下属事业编制单位，此次会议的决定并未形成文件下发，但是每年军转干部的安置均按照这个决议执行，引发后续35名军转干部不满，认为这个决议违背了国家军转安置政策的相关规定，是个错误决定，要求上级部门撤销这个决定，将35名军转干部的身份转为公务员。12:40分许，省发改委一名副主任带领工作组来到省政府信访局接待来访军转干部，但上述人员要求与发改委主任对话。相关情况密切关注中。 </span></p>\n', '2018-10-18 11:20:00', '2018-10-18 17:00:00', 5.67, 25, '身份待遇', '华山东路派出所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-10-19 09:57:29', '2018-10-31 10:56:12', NULL, '退伍民兵', 'qkfy', NULL, NULL, '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 110, NULL, 2392, 2526, 'cz', '城镇', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'jtry', '军退人员', 'tyaz,gzfldy', '退役安置,工资福利待遇', 'sldws', '16-50人', '2', NULL, NULL, 0.00, 0, 0, 'yb', NULL, NULL, '82fad43f-3061-452e-8981-02103a9553f3', 'JS', NULL, 'Y', 110, 151951, 'zzwd', '530102450000', '李敏', '吴文', NULL, '吴文', 5016, 'ZQYSB', 'N', NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (5019, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'bq', '2018-10-18 11:25:33', '10月18日10余名东风云南汽车厂退休职工聚集在汽车厂', '<p><span style=\"font-size:24px\">10月18日09时05分许，90余名东风云南汽车厂的退休职工分散聚集在云南汽车厂大门两侧人行道上，要求东风云南汽车厂解决他们的问题及诉求，在下厂区大门口侧边栏杆处、门上分别横挂4幅内容相同的白底黑字布标，内容为&ldquo;要求公开土地置换合同、严查国有土地流失和国有资产流失&rdquo;。&ldquo;严惩贪污腐败，我们要吃饭、我们要生存、还我血汗钱。&rdquo; 同时有90余名汽车厂职工聚集在厂区内要求东风云南汽车厂归还土地，民警在现场维持秩序，未出现堵门堵路情况，无过激行为。至11时20分许聚集职工自行收拾标语离开，相关情况持续关注中。（黑林铺所）</span></p>\n', '2018-10-18 09:05:00', '2018-10-18 11:20:00', 2.25, 90, '身份待遇', '黑林铺派出所', '东风云南汽车厂', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-10-19 09:57:29', '2018-10-31 10:55:46', NULL, '退休职工', 'qkfy', NULL, NULL, '拉横幅', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 110, NULL, 2606, 2556, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', NULL, NULL, 0.00, 0, 0, 'yb', NULL, NULL, 'c7e243b7-3953-4c6b-8e5d-443c4ffd212b', 'JS', NULL, 'Y', 110, 151951, 'zzwd', '530102570000', '秦怀国', '吴文', NULL, '吴文', 5018, 'ZQYSB', 'N', NULL);
