INSERT INTO `qb_zdasjqbxx` VALUES (5076, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'bs', '2018-10-22 15:45:38', '10月22日在金冠食品厂做工的农民工5人因工钱被拖欠到省政府上访', '<p><span style=\"font-size:22px\">10月22日14:10分许，5名小板桥镇羊肠村村民到省政府上访，反映，她们一共7人两个月前在位于经开区小喜村的金冠食品厂做临时工，每人每天工钱80元，至今已做了近2个月，但工钱一直未完全拿到，还剩25000余元未领到，因多次向雇主讨要未找到雇主所以到省政府上访，要求尽快拿到工钱。省信访局接待后告知其回经开区政府反映。14:35分许，5人全部离开省政府。(华山所)</span></p>\n', '2018-10-22 14:10:00', '2018-10-22 14:35:00', 0.42, 5, '讨薪索款', '华山东路派出所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-10-31 09:50:26', '2018-10-31 11:02:54', NULL, '村民', 'qkfy', NULL, NULL, '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 110, NULL, 2392, 2507, 'cz', '城镇', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', 'lzjf', '劳资纠纷', 'sryx', '10人以下', '1', NULL, NULL, 0.00, 0, 0, 'yb', NULL, NULL, '9de13323-f01a-47d7-9a8a-9f5fe5ae4f33', 'JS', NULL, 'Y', 110, 151951, 'zzwd', '530102450000', '李敏', '吴文', NULL, '吴文', 5075, 'ZQYSB', 'N', NULL);
