INSERT INTO `qb_zdasjqbxx` VALUES (5082, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'bq', '2018-10-24 14:11:46', '24日30余名东风云南汽车厂退休职工例行到厂区门口聚集', '<p><span style=\"font-size:22px\">10月24日09时04分许，30余名东风云南汽车厂的退休职工再次到厂区大门口聚集，要求东风云南汽车厂解决他们的问题及诉求，在下厂区大门口侧边栏杆处、门上分别横挂6幅白底黑字布标，内容为&ldquo;要求公开土地置换合同、严查国有土地流失和国有资产流失&rdquo;。&ldquo;严惩贪污腐败，我们要吃饭、我们要生存、还我血汗钱。&rdquo; &ldquo;云汽土地置换项目必须由真正的云汽职工代表全程参与！ &rdquo; &ldquo;强烈要求胡伟民回云汽，给当年被强制买断员工一个说法！ &rdquo;。 黑林铺所民警到现场维护秩序，聚集职工无过激行为。 至10时40分许聚集职工自行收拾标语离开，未出现堵路及影响交通的情况。相关情况持续关注中。（黑林铺所）</span></p>\n', '2018-10-24 09:04:00', '2018-10-24 10:40:00', 1.60, 30, '身份待遇', '黑林铺派出所', '东风云南汽车厂', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-10-31 09:56:49', '2018-10-31 11:30:58', NULL, '退休职工', 'qkfy', NULL, NULL, '拉横幅', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 110, NULL, NULL, 2556, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', NULL, NULL, 0.00, 0, 0, 'yb', NULL, NULL, 'e31eb720-23c4-4578-9f70-ab0e7fb271dd', 'JS', NULL, 'Y', 110, 151951, 'zzwd', '530102570000', '栗斌', '吴文', NULL, '吴文', 5081, 'ZQYSB', 'N', NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (5084, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'bq', '2018-10-24 14:13:21', '10月24日原云南省机械工业厅招待所退休职工5人因工资被扣发到省政府上访', '<p><span style=\"font-size:22px\">24日11:15分许，5名原云南省机械工业厅（现改名为云南省机械工业行业协会）招待所退休职工到省政府上访，反映，他们在单位工作时是机关单位行政编制，但退休时单位却把他们按照企业人员待遇退休，致使他们的退休金每月只有2000余元，一共涉及9名退休职工，在退休金中，有一项退休补助，每月100余元，来访人员称，这项补助从上个月起被单位莫名取消，引发9人不满，由于该单位历经多次机构改革，现他们不清楚该向哪个部门反映，所以到省政府上访，要求恢复这项补助。省信访局接待后告知其向云南省工信委反映。11:40分许，5人全部离开省政府。</span></p>\n', '2018-10-24 11:15:00', '2018-10-24 11:40:00', 0.42, 5, '身份待遇', '华山东路派出所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-10-31 09:56:49', '2018-10-31 11:36:18', NULL, '退休职工', 'qkfy', NULL, NULL, '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 110, NULL, 2392, 2556, 'cz', '城镇', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'ltxry', '离退休人员', 'gzfldy', '工资福利待遇', 'sryx', '10人以下', '1', NULL, NULL, 0.00, 0, 0, 'yb', NULL, NULL, 'e196a669-89b4-4af2-872a-31cc59e57580', 'JS', NULL, 'Y', 110, 151951, 'zzwd', '530102450000', '李敏', '李利宏', NULL, '李利宏', 5083, 'ZQYSB', 'N', NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (5086, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'bq', '2018-10-24 11:22:51', '10月24日丘北县八道哨乡村民7人因烟叶收购问题到省政府上访', '<p><span style=\"font-size:22px\">24日09:30分许，7名文山州丘北县八道哨乡豹子坡村委会村民到省政府上访，反映，去年他们按照乡镇的安排种植烤烟，现烟叶已收割完毕，但烟站没有完全收购村民的烟叶，至今仍有80余户村民的6万公斤烟叶被拒收，烟农为此受到损失，他们认为种植烟叶是政府安排的，应该全部收购，村民曾就此问题向当地政府反映，要求全部收购烟叶未果所以到省政府上访，要求上级政府协调尽快解决烟叶收购问题。省信访局接待后告知其回文山州反映。10:05分许，7人全部离开省政府。(华山所)</span></p>\n', '2018-10-24 09:30:00', '2018-10-24 10:05:00', 0.58, 7, '损害赔偿 ', '华山东路派出所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-10-31 09:56:49', '2018-10-31 11:19:11', NULL, '村民', 'qkfy', NULL, NULL, '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 110, NULL, 2392, 2507, 'nn', '农村', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', 'snwt', '三农问题', 'sryx', '10人以下', '1', NULL, NULL, 0.00, 0, 0, 'yb', NULL, NULL, '87ad36f5-24a4-4799-872e-3a6d51da2e23', 'JS', NULL, 'Y', 110, 151951, 'zzwd', '530102450000', '李敏', '吴文', NULL, '吴文', 5085, 'ZQYSB', 'N', NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (5088, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'bs', '2018-10-25 16:24:32', '楚雄州各地购车者6人因利益受损到省政府上访', '<p><span style=\"font-size:20px\">25日16:00许，6名楚雄州居民到省政府上访，反映，从 去年底起，他们陆续参与了&ldquo;云南汇和商务服务有限公司&rdquo;的共享购车活动，该公司位于昆明市盘龙区北京路财智心景大厦18楼，来访人员称该公司的购车模式是共享互联网 ，即每人缴纳2.2万元后成为公司会员，如果该人发展3名人员并且这3人每人向公司同样缴纳2.2万元后，则该人可提取一辆价值9.5万元的汽车，以此类推，目前已知楚雄州参与此项活动的人员有600余人。今年3月29日，盘龙公安分局突然查封该公司，导致购车者的车款被冻结，公司承诺的汽车没有提到，引发参与者不满，多次向相关部门反映要求拿回缴纳的资金，由于一直没有得到答复所以到省政府上访，要求上级政府协调解决。省信访局接待后告知其向盘龙公安分局经侦大队反映。16:15分许，6人全部离开省政府。&nbsp;</span></p>\n', '2018-10-25 16:00:00', '2018-10-25 16:15:00', 0.25, 6, '劳资纠纷', '华山东路派出所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-10-31 10:00:25', '2018-10-31 16:13:08', NULL, '村民', 'qkfy', NULL, NULL, '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 110, NULL, 2392, 2507, 'cz', '城镇', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'tsqt', '特殊群体', 'zfwt', '执法问题', 'sryx', '10人以下', '1', NULL, NULL, 0.00, 0, 0, 'yb', NULL, NULL, '352fc9c8-6f79-4c63-a1a3-ee23be3cc7fc', 'JS', NULL, 'Y', 110, 151951, 'zzwd', '530102450000', '李敏', '吴文', NULL, '吴文', 5087, 'ZQYSB', 'N', NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (5090, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'bq', '2018-10-25 15:45:30', '丘北县天星乡村民6人因烟叶收购问题到省政府上访', '<p><span style=\"font-size:22px\">25日15:05分许，6名文山州丘北县天星乡天星村村民到省政府上访，反映，去年他们按照乡镇的安排种植烤烟，现烟叶已收割完毕，但烟站没有完全收购村民的烟叶，至今仍有100余户村民的5万公斤烟叶被拒收，烟农为此受到损失，他们认为种植烟叶是政府安排的，应该全部收购，村民曾就此问题向当地政府反映，要求尽快全部收购烟叶未果所以到省政府上访，要求上级政府协调尽快解决烟叶收购问题。省信访局接待后告知其回文山州反映。15:30分许，6人全部离开省政府。</span></p>\n', '2018-10-25 15:05:00', '2018-10-25 15:30:00', 0.42, 6, '损害赔偿 ', '华山东路派出所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-10-31 10:00:25', '2018-11-02 09:28:25', NULL, '村民', 'qkfy', NULL, NULL, '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 110, NULL, 2392, 2507, 'nn', '农村', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', 'snwt', '三农问题', 'sryx', '10人以下', '1', NULL, NULL, 0.00, 0, 0, 'yb', NULL, NULL, '39645ab4-3d94-4757-bc17-f85adec36798', 'JS', NULL, 'Y', 110, 151951, 'zzwd', '530102450000', '李敏', '吴文', NULL, '吴文', 5089, 'ZQYSB', 'N', NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (5092, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'bq', '2018-10-25 14:47:31', '30余名东风云南汽车厂退休职工聚集在滇缅大道东风云南汽车厂门口', '<p><span style=\"font-size:22px\">10月25日09时20分许，30余名东风云南汽车厂的退休职工分散聚集在云南汽车厂下厂区大门两侧人行道上及厂区大门口，要求东风云南汽车厂解决他们的问题及诉求，在下厂区大门口侧边栏杆处、门上分别横挂6幅白底黑字布标，内容为&ldquo;要求公开土地置换合同、严查国有土地流失和国有资产流失&rdquo;。&ldquo;严惩贪污腐败，我们要吃饭、我们要生存、还我血汗钱。&rdquo; &ldquo;云汽土地置换项目必须由真正的云汽职工代表全程参与！ &rdquo; &ldquo;强烈要求胡伟民回云汽，给当年被强制买断员工一个说法！ &rdquo;。并在汽车厂大门附近粘贴相关维权材料，我所民警到现场维护秩序，后汽车厂职工分散聚集在厂区门口和两旁人行道上，无过激行为。至10时55分许聚集职工自行30收拾标语离开，未出现堵路及影响交通的情况。相关情况持续关注中。</span></p>\n', '2018-10-25 09:20:00', '2018-10-25 10:55:00', 1.58, 0, '身份待遇', '黑林铺派出所', '东风云南汽车厂', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-10-31 10:00:25', '2018-10-31 15:57:57', NULL, '退休职工', 'qkfy', NULL, NULL, '拉横幅', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 110, NULL, 2606, 2556, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', NULL, NULL, 0.00, 0, 0, 'yb', NULL, NULL, 'cdf20fe8-ccc8-43f2-919c-d25ccabcd9e2', 'JS', NULL, 'Y', 110, 151951, 'gayw', '530102570000', '栗斌', '吴文', NULL, '吴文', 5091, 'ZQYSB', 'N', NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (5094, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'bq', '2018-10-25 10:12:51', '安徽籍妇女柯彩梅第三次到省政府信访局非正常上访', '<p><span style=\"font-size:24px\">24日13:40分许，安徽籍妇女柯彩梅带领两名女儿第三次来到省政府信访局，反映其丈夫触电死亡的赔偿问题。其称今早到昆明市行政中心司法援助部门进行调解，自己要求供电局赔偿80万，供电局愿意赔偿60万，由于双方分歧较大，柯彩梅再次来到省政府，要求省政府支持其诉求。省信访局再次接待3人，明确告知其如果协商不成只能上诉法院进行判决。柯彩梅不听劝说，于15:50分许带领两女再次来到省政府大门口，披麻戴孝喊冤，民警立即处置，将其孝布收缴并严肃警告其要依法依规反映诉求，并将其劝离。相关情况进一步掌握中。</span></p>\n', '2018-10-24 13:40:00', '2018-10-24 15:50:00', 2.17, 3, '损害赔偿 ', '华山东路派出所', '省信访局', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-10-31 10:00:25', '2018-10-31 14:30:08', NULL, '普通市民', 'qkfy', NULL, NULL, '披麻戴孝喊冤', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 110, NULL, 2662, 2553, 'cz', '城镇', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', 'mjjf', '民间纠纷', 'sryx', '10人以下', '2', NULL, NULL, 600000.00, 1, 0, 'yb', NULL, NULL, '746ae1cf-ec78-4bc6-b5ab-cb0af273501b', 'JS', NULL, 'Y', 110, 151951, 'zzwd', '530102450000', '李敏', '吴文', NULL, '吴文', 5093, 'ZQYSB', 'N', NULL);
