INSERT INTO `qb_zdasjqbxx` VALUES (5098, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'bq', '2018-10-26 11:41:44', '30余名东风云南汽车厂退休职工聚集在滇缅大道东风云南汽车厂门前', '<p><span style=\"font-size:22px\">10月26日9时许，30余名东风云南汽车厂的退休职工分散聚集在云南汽车厂下厂区大门两侧人行道上及厂区大门口，要求东风云南汽车厂解决他们的问题及诉求，在下厂区大门口侧边栏杆处、门上分别横挂6幅白底黑字布标，内容为&ldquo;要求公开土地置换合同、严查国有土地流失和国有资产流失&rdquo;。&ldquo;严惩贪污腐败，我们要吃饭、我们要生存、还我血汗钱。&rdquo; &ldquo;云汽土地置换项目必须由真正的云汽职工代表全程参与！ &rdquo; &ldquo;强烈要求胡伟民回云汽，给当年被强制买断员工一个说法！ &rdquo;。并在汽车厂大门附近粘贴相关维权材料，我所民警到现场维护秩序，后汽车厂职工分散聚集在厂区门口和两旁人行道上，无过激行为。至10时25分许聚集职工自行收拾标语离开，未出现堵路及影响交通的情况。相关情况持续关注中。</span></p>\n', '2018-10-26 09:00:00', '2018-10-26 10:25:00', 1.42, 30, '身份待遇', '黑林铺派出所', '东风云南汽车厂', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-10-31 10:01:02', '2018-10-31 16:42:09', NULL, '退休职工', 'qkfy', NULL, NULL, '拉横幅', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 110, NULL, 2606, 2556, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', NULL, NULL, 0.00, 0, 0, 'yb', NULL, NULL, 'eed9cebb-fef4-49c4-a555-0edf1d8e2d11', 'JS', NULL, 'Y', 110, 151951, 'gayw', '530102570000', '栗斌', '吴文', NULL, '吴文', 5097, 'ZQYSB', 'N', NULL);
