INSERT INTO `qb_zdasjqbxx` VALUES (5106, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'bs', '2018-10-29 14:03:11', '禄丰县碧城镇村民52人因温泉水资源问题到省政府上访', '<p><span style=\"font-size:20px\">29日11时20分许，楚雄州禄丰县碧城镇洪流村委会温泉村52名村民到省政府上访，代表全村90余户村民反映，该村以温泉著名，每家村民均自行挖温泉招待游客，根据用水量的多少每年向相关部门缴纳管理费。今年7月份，相关部门进行温泉地热水治理，在未召开村民大会的情况下，该村16户村民自行成立了温泉协会，每家出资30万元，共计480万元购买了温泉水使用许可证，垄断了温泉用水，其他村民用水每方需向该协会缴纳10.2元，引发其他村民不满，质疑该协会的合法性，并多次向相关部门反映要求撤销该协会，由于未得到答复故到省政府上访，要求撤销该协会，将许可证办理为村集体共有。该起村民于9月28日曾就相同诉求到访过，由于问题一直没有得到答复所以今日再次到省政府上访，省信访局接待后告知其根据信访条例的规定，相同信访事项两个月之内不再重复接待，要求其回楚雄州反映。由于对接待结果不满，50余人现滞留在接访大厅，声称不解决就不离开。相关情况密切关注中。</span></p>\n', '2018-10-29 11:20:00', '2018-10-29 12:00:00', 0.67, 52, '涉法涉诉', '华山东路派出所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-10-31 10:51:17', '2018-11-01 11:15:40', NULL, '村民', 'qkfy', NULL, NULL, '无 ', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 110, NULL, 2392, 2507, 'nn', '农村', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', 'mjjf', '民间纠纷', 'wsyr', '51-100人', '2', NULL, NULL, 0.00, 0, 0, 'yb', NULL, NULL, '7ba6aa76-80d3-4052-b711-966cfc4e230d', 'JS', NULL, 'Y', 110, 151951, 'zzwd', '530102450000', '李敏', '吴文', NULL, '吴文', 5105, 'ZQYSB', 'N', NULL);
