INSERT INTO `qb_zdasjqbxx` VALUES (5120, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'bq', '2018-10-31 13:58:00', '10月31日50余名东风云南汽车厂退休职工聚集在汽车厂门口', '<p><span style=\"font-size:20px\">10月31日08时55分许，30余名东风云南汽车厂的退休职工分散聚集在云南汽车厂上下厂区大门两侧人行道上及厂区大门口，并陆续有职工到此聚集，10时14分许，约有50名退休职工聚集在上下厂区大门口，要求东风云南汽车厂解决他们的问题及诉求，在下厂区大门口侧边栏杆处、门上分别横挂6幅白底黑字布标，内容为&ldquo;要求公开土地置换合同、严查国有土地流失和国有资产流失&rdquo;。&ldquo;严惩贪污腐败，我们要吃饭、我们要生存、还我血汗钱。&rdquo; &ldquo;云汽土地置换项目必须由真正的云汽职工代表全程参与！ &rdquo; &ldquo;强烈要求胡伟民回云汽，给当年被强制买断员工一个说法！ &rdquo;。并将上下厂区大门堵住，不许车辆出入，至11时30分许聚集职工自行收拾标语离开，无过激行为。相关情况持续关注中。（黑林铺所）</span></p>\n', '2018-10-31 08:55:00', '2018-10-31 11:30:00', 2.58, 0, '身份待遇', '黑林铺派出所', '东风云南汽车厂', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-11-01 10:49:32', '2018-11-01 16:36:24', NULL, '退休职工', 'qkfy', NULL, NULL, '拉横幅、堵路', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 110, NULL, 2606, 2556, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', NULL, NULL, 0.00, 0, 0, 'yb', NULL, NULL, '643d2bce-94eb-49f1-ba33-6a2cd6afeb3f', 'JS', NULL, 'Y', 110, 151951, 'gayw', '530102570000', '秦怀国', '吴文', NULL, '吴文', 5119, 'ZQYSB', 'N', NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (5122, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'bq', '2018-10-31 14:00:50', '碧桂园都荟小区业主拉横幅反对幼儿园入驻', '<p><span style=\"font-size:22px\">位于小康大道北段碧桂园都荟小区，共建357套住房，现已入住150余户。2018年中旬，小区开发商与昆明立品教育集团合作，拟在小区开设幼儿园。此举遭到73户小区业主联名反对，双方多次沟通协商无果。10月30日晚，部分业主在小区内墙围栏、公共绿地及小区商铺门口悬挂&ldquo;三年幼儿园 祸害一辈子&rdquo;、&ldquo;堵消防，有噪音，反对都荟建幼儿园&rdquo;、&ldquo;吵闹、拥挤、践踏、破坏，幼儿园给你一个无星级的家！&rdquo;3条横幅。经工作，横幅已取下。民警走访后获悉，部分业主认为购房时开发方并未告知小区将有幼儿园入驻，并表示幼儿园入驻会给自己生活带来很大影响，如开发方执意在小区内开设幼儿园，将采取聚集堵门、上访等方式维护权益。相关情况进一步掌握中。</span></p>\n', '2018-10-30 20:00:00', '2018-10-30 21:00:00', 1.00, 73, '产权物业', '红云派出所', '碧桂园都荟小区', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-11-01 10:49:32', '2018-11-01 16:40:34', NULL, '业主购房者', NULL, NULL, NULL, '拉横幅', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 110, NULL, NULL, 2528, NULL, NULL, 'gzfy', '工作反映', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', NULL, NULL, 0.00, 0, 0, NULL, NULL, NULL, 'f25c054b-8901-4fae-995b-14b084144f76', 'JS', NULL, 'Y', 110, 151951, 'gayw', '530102490000', '段文', '吴文', NULL, '吴文', 5121, 'ZQYSB', 'N', NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (5124, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'bq', '2018-10-31 14:57:45', '50余商铺业主在一号广场拉横幅维权', '<p><span style=\"font-size:20px\">10月31日9时40分许，一号广场A地块约50名商铺业主，将原一号广场售楼部围住，拉起&ldquo;只差消防跪求政府为民做主&rdquo;、&ldquo;黑心开发商勾结利益方为私利挪用消防款项&rdquo;等4条横幅，并用喇叭播放横幅内容。据聚集业主称，由于商铺消防未验收，致无法按时接房，造成亏损。现开发商将消防验未收的售楼部租赁给恒大地产做展厅，之前消防未验收不能接房完全是借口。经分局工作，昆都国际地产开发有限公司与业主约定11月1日再进行协商。业主于14时许自行离开。相关情况进一步掌握中。</span></p>\n', '2018-10-31 09:40:00', '2018-10-31 14:00:00', 4.33, 50, '产权物业', '茭菱派出所', '一号广场', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-11-01 10:49:32', '2018-11-01 16:47:02', NULL, '壹号广场购房者', 'qkfy', NULL, NULL, '拉横幅', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 110, NULL, NULL, 2532, NULL, NULL, 'gzfy', '工作反映', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', NULL, NULL, 0.00, 0, 0, 'yb', NULL, NULL, 'f90dc4a3-8b7f-4f48-8036-088d4b00b095', 'JS', NULL, 'Y', 110, 151951, 'gayw', '530102550000', '杨爱琼', '吴文', NULL, '吴文', 5123, 'ZQYSB', 'N', NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (5128, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'bs', '2018-10-29 10:41:11', '昆明市各企业自办学校退休教师210余人因待遇问题到省政府上访（续报）', '<p><span style=\"font-size:22px\">29日08:35分许，昆明市各区企业自办学校的退休教师因待遇问题到省政府上访，截止09:30分许，来访人员达210余人，主要来自以下单位：云南轴承厂、云南仪表厂、云南印染厂、昆明电机厂、昆明冶炼厂、昆明电缆厂、云南纺织厂、昆明制药厂、云南可保煤矿学校、昆明电化厂、解放军7434厂学校、云南汽车厂、昆明平板玻璃厂、昆明轻机厂、昆明重机厂、解放军7321工厂学校、昆明十四冶、昆明轧钢厂、云南铸造厂、昆钢一中、昆钢四中、昆钢一小、昆钢三小、昆钢四小、昆钢桥钢子弟学校、昆钢八街矿学校、云南cy集团云南砖瓦总厂、云南新型建筑材料厂。现省信访局已通知昆明市相关部门负责人到省信访局接待来访群众。目前200余人正在接访大厅等待接待，秩序平稳。</span></p>\n', '2018-10-29 08:35:00', '2018-10-29 14:00:00', 5.42, 210, '身份待遇', '五华分局', NULL, NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-11-01 10:53:14', '2018-11-21 15:19:09', NULL, '民办、代课教师', 'qkfy', NULL, NULL, '无 ', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 110, NULL, NULL, 2545, 'cz', '城镇', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'ltxry,jsxs', '离退休人员,教师学生', 'gzfldy', '工资福利待遇', 'yblyr', '101-200人', '2', NULL, NULL, 0.00, 0, 0, 'yb', NULL, NULL, 'ef48b788-f706-4cbd-ad16-ab09555de6b2', 'JS', NULL, 'Y', 110, 151951, 'zzwd', '530102000000', '李敏', '吴文', NULL, '吴文', 5127, 'ZQYSB', 'N', NULL);
