INSERT INTO `qb_zdasjqbxx` VALUES (5134, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'bs', '2018-11-01 15:00:45', '丘北县曰者镇村民11人因烟叶收购问题到省政府上访', '<p><span style=\"font-size:22px\">1日14:20分许，11名文山州丘北县曰者镇曰者村委会村民到省政府上访，反映，去年他们按照乡镇的安排种植烤烟，现烟叶已收割完毕，但烟站没有完全收购村民的烟叶，至今仍有21户村民种植的25吨烟叶被拒收，烟农为此受到损失，他们认为种植烟叶是政府安排的，应该全部收购，村民曾就此问题向当地政府反映，要求尽快全部收购烟叶未果所以到省政府上访，要求上级政府协调尽快解决烟叶收购问题。省信访局接待后告知其回文山州反映。14:40分许，11人全部离开省政府。</span></p>\n', '2018-11-01 14:20:00', '2018-11-01 14:40:00', 0.33, 11, '损害赔偿 ', '华山东路派出所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-11-02 09:22:02', '2018-11-02 09:29:37', NULL, '村民', 'qkfy', NULL, NULL, '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 110, NULL, 2392, 2507, 'nn', '农村', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', 'snwt', '三农问题', 'syr', '11-15人', '1', NULL, NULL, 0.00, 0, 0, 'yb', NULL, NULL, 'e7f37e9f-bca5-4b11-b6fe-ecd1ed3c7782', 'JS', NULL, 'Y', 110, 151951, 'zzwd', '530102450000', '李敏', '吴文', NULL, '吴文', 5133, 'ZQYSB', 'N', NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (5138, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'bq', '2018-11-01 11:21:02', '11月01日10余名东风云南汽车厂退休职工聚集在汽车厂门口', '<p><span style=\"font-size:22px\">11月01日09时23分许，10余名东风云南汽车厂的退休职工分散聚集在云南汽车厂上下厂区大门两侧人行道上及厂区大门口，并陆续有职工到此聚集，要求东风云南汽车厂解决他们的问题及诉求，在下厂区大门口侧边栏杆处、门上分别横挂6幅白底黑字布标，内容为&ldquo;要求公开土地置换合同、严查国有土地流失和国有资产流失&rdquo;。&ldquo;严惩贪污腐败，我们要吃饭、我们要生存、还我血汗钱。&rdquo; &ldquo;云汽土地置换项目必须由真正的云汽职工代表全程参与！ &rdquo; &ldquo;强烈要求胡伟民回云汽，给当年被强制买断员工一个说法！ &rdquo;。退休职工将上下厂区大门堵住，不许车辆出入，民警到现场维持秩序，并协调厂内人员若有紧急情况从侧门出入，至10时55分许聚集职工自行收拾标语离开。相关情况持续关注中。（黑林铺所）</span></p>\n', '2018-11-01 09:23:00', '2018-11-01 10:55:00', 1.53, 10, '身份待遇', '黑林铺派出所', '东风云南汽车厂', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-11-02 09:22:02', '2018-11-02 09:26:05', NULL, '退休职工', 'qkfy', NULL, NULL, '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 110, NULL, 2606, 2556, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', NULL, NULL, 0.00, 0, 0, 'yb', NULL, NULL, '95d6b2fb-5e40-4c7e-959e-5851a17d955b', 'JS', NULL, 'Y', 110, 151951, 'gayw', '530102570000', '秦怀国', '吴文', NULL, '吴文', 5137, 'ZQYSB', 'N', NULL);
