INSERT INTO `qb_zdasjqbxx` VALUES (5171, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'bs', '2018-11-05 10:48:33', '11月5日宣威市田坝镇村民15 人土地补偿问题到省政府上访', '<p><span style=\"font-size:22px\">11月5日10时30分许，15名宣威市田坝镇新发村委会村民到省政府上访，反映，由于修建万口电站的需要，今年7月份库区淹没了该村部分农田，村民了解到，电站已将补偿款拨到镇政府账户，但镇政府一直没有发给村民，引发村民不满，要求尽快发放补偿。由于在当地反映未得到解决所以到省政府上访，要求镇政府尽快将补偿款发给村民。现信访接待中。（华山所）</span></p>\n', '2018-11-05 10:30:00', '2018-11-05 11:00:00', 0.50, 15, '损害赔偿 ', '华山东路派出所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-11-07 08:57:20', '2018-11-07 10:02:18', NULL, '村民', 'qkfy', NULL, NULL, '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 110, NULL, 2392, 2507, 'nn', '农村', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', 'snwt', '三农问题', 'syr', '11-15人', '2', NULL, NULL, 0.00, 0, 0, 'yb', NULL, NULL, '4867be7b-21f9-4b9f-82ea-6b9b9e25084b', 'JS', NULL, 'Y', 110, 151951, 'zzwd', '530102450000', '李敏', '吴文', NULL, '吴文', 5170, 'ZQYSB', 'N', NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (5173, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'bse', '2018-11-05 10:46:33', '11月5日广南县南屏镇村民9人因不满农田性质被改到省政府上访', '<p><span style=\"font-size:22px\">11月5日09时45分许，9名文山州广南县南屏镇马街村委会村民到省政府上访，反映，一年前因修建公路的需要，当地政府征用了该村的土地并对村民进行了赔偿，现政府还要再征收公路两旁的200余亩土地，来访村民称，当地政府这次征地不是为了修路而是将土地卖给开发商搞开发，给予村民的补偿是每亩39800元，部分村民同意，仍有9户村民不同意，他们认为此次征用的是基本农田，国家有相关规定不允许征用基本农田，但当地政府强制将这些土地的性质进行变更，然后高价卖给开发商，引发9户村民不满，坚决不同意变更农田性质，并多次向政府有关部门反映，要求当地政府撤销这种违规行为，由于得不到满意答复所以到省政府上访，投诉当地政府的违规行为，要求将土地性质变更回基本农田。现信访接待中。（华山所）</span></p>\n', '2018-11-05 09:45:00', '2018-11-05 10:20:00', 0.58, 9, '征地赔偿', '华山东路派出所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-11-07 08:57:20', '2018-11-07 09:55:46', NULL, '村民', 'qkfy', NULL, NULL, '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 110, NULL, NULL, 2507, 'nn', '农村', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', 'snwt', '三农问题', 'sryx', '10人以下', '2', NULL, NULL, 0.00, 0, 0, 'yb', NULL, NULL, '0db1507d-8159-4b88-aa40-06103f0e0397', 'JS', NULL, 'Y', 110, 151951, 'zzwd', '530102450000', '李敏', '吴文', NULL, '吴文', 5172, 'ZQYSB', 'N', NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (5175, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'bs', '2018-11-05 10:37:14', '11月5日曲靖市麒麟区三宝镇村民8人因安置问题到省政府上访', '<p><span style=\"font-size:22px\">11月 5日09时40分许，8名曲靖市麒麟区三宝镇何旗居委会徐家山村村民到省政府上访，反映，今年初当地政府告知村民，修建的寥廓南路延长线规划经过该村，要求该村整体搬迁，村民称，政府要求100余户村民先自行找居住地，政府给予过渡费，一年后再安置，村民不同意，要求政府先将村民安置好后再拆迁。由于双方存在争议协商不了故到省政府上访，要求上级政府支持村民诉求，先安置再拆迁。省信访局接待后告知其回曲靖市反映。10时15分许，8人全部离开省政府。（华山所）</span></p>\n', '2018-11-05 09:40:00', '2018-11-05 10:15:00', 0.58, 8, '拆迁安置', '华山东路派出所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-11-07 08:57:20', '2018-11-07 09:52:05', NULL, '村民', 'qkfy', NULL, NULL, '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 110, NULL, 2392, 2507, 'nn', '农村', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', 'snwt', '三农问题', 'sryx', '10人以下', '1', NULL, NULL, 0.00, 0, 0, 'yb', NULL, NULL, '02e61d35-892a-4320-a8a8-00c5ef9e2bf9', 'JS', NULL, 'Y', 110, 151951, 'zzwd', '530102450000', '李敏', '吴文', NULL, '吴文', 5174, 'ZQYSB', 'N', NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (5177, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'bs', '2018-11-06 14:41:54', '11月6日部分我市涉金融受损群体成员到省委聚集上访', '<p><span style=\"font-size:24px\">11月6日10时许，陆续有我市涉金融受损群体&ldquo;儒商&rdquo;、&ldquo;博融&rdquo;、&ldquo;互信&rdquo;等群体的60余名群体成员到云南省委聚集上访，聚集过程中，部分群体成员向省委信访局递交了题为《云南省极速返贫人群请求扶贫救助的申请》的诉求材料，通过现场工作人员做工作，部分人员被现场工作人员劝离，另有部分人员被分流志五华体育馆。11时许，上访人员陆续离开现场。相关情况在进一步掌握中。（国保大队）</span></p>\n', '2018-11-06 10:00:00', '2018-11-06 11:00:00', 1.00, 60, '特殊群体', '西山分局', '云南省委', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-11-07 09:45:40', '2018-11-07 10:13:28', NULL, '小微儒商', 'qkfy', NULL, NULL, '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 110, NULL, NULL, 2503, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', NULL, NULL, 0.00, 0, 0, 'yb', NULL, NULL, 'd15fd0b6-9457-460d-829b-3b8269339f9b', 'JS', NULL, 'Y', 110, 151951, 'gayw', '530105000000', '徐治强', '吴文', NULL, '吴文', 5176, 'ZQYSB', 'N', NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (5179, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'bq', '2018-11-06 11:12:23', '20余名东风云南汽车厂退休职工聚集在滇缅大道东风云南汽车厂门口', '<p><span style=\"font-size:20px\">11月6日9时许，20余名东风云南汽车厂的退休职工分散聚集在云南汽车厂上下厂区大门两侧人行道上及厂区大门口，并陆续有职工到此聚集，要求东风云南汽车厂解决他们的问题及诉求，在下厂区大门口侧边栏杆处、门上分别横挂6幅白底黑字布标，内容为&ldquo;要求公开土地置换合同、严查国有土地流失和国有资产流失&rdquo;。&ldquo;严惩贪污腐败，我们要吃饭、我们要生存、还我血汗钱。&rdquo; &ldquo;云汽土地置换项目必须由真正的云汽职工代表全程参与！ &rdquo; &ldquo;强烈要求胡伟民回云汽，给当年被强制买断员工一个说法！ &rdquo;。至10时37分许聚集职工自行收拾标语离开，无过激行为，未堵门堵路。相关情况持续关注中。</span></p>\n', '2018-11-06 09:00:00', '2018-11-06 10:37:00', 1.62, 20, '身份待遇', '黑林铺派出所', '东风云南汽车厂', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-11-07 09:45:40', '2018-11-07 10:09:29', NULL, '退休职工', 'qkfy', NULL, NULL, '拉横幅、堵路', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 110, NULL, 2606, 2556, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', NULL, NULL, 0.00, 0, 0, 'yb', NULL, NULL, '333e2083-f75b-4270-90d7-69afcd8a3cdc', 'JS', NULL, 'Y', 110, 151951, 'gayw', '530102570000', '秦怀国', '吴文', NULL, '吴文', 5178, 'ZQYSB', 'N', NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (5181, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'bq', '2018-11-06 09:16:21', '5名新源再生产业有限公司职工聚集在新源阁酒店', '<p><span style=\"font-size:22px\">11月05日，云南新源再生产业有限公司7名退休职工在新源阁酒店聚集，要求诺嘉公司归还新源酒店的经营权，退休员工分布在酒店二楼房间内看电视、聊天等，并在新源酒店留宿，相关情况持续关注中。</span></p>\n', '2018-11-05 09:00:00', '2018-11-05 00:00:00', 9.00, 0, '合同纠纷 ', '黑林铺派出所', '新源阁酒店', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-11-07 09:45:40', '2018-11-07 10:08:00', NULL, '退休职工', 'qkfy', NULL, NULL, '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 110, NULL, 2758, 2556, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', NULL, NULL, 0.00, 0, 0, 'yb', NULL, NULL, '8a203440-4df6-447d-8ea2-87efbc0b1a74', 'JS', NULL, 'Y', 110, 151951, 'gayw', '530102570000', '洪林锋', '吴文', NULL, '吴文', 5180, 'ZQYSB', 'N', NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (5187, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'bs', '2018-11-07 11:23:02', '7日西山区马家营村民5人因统建房问题到省政府上访', '<p>7日10:15分许，5名西山区永昌街道办事处马家营村村民到省政府上访，反映，1999年，有关部门批准了该村47亩土地用于统建房，但是，在建房过程中，村小组成员将部分土地以20万元一亩的价格卖给永宝盛房地产开发有限公司，建盖成临街商铺及商住楼，导致其他村民住房面积严重缩水，另外卖地的收入村小组不公开，部分村民没有回迁也没有拿到过渡费等问题，引发村民不满，多次向有关部门反映要求公开村内账目一直没有得到答复所以到省政府上访，要求解决该村管理混乱、过渡费以及回迁安置等问题。现信访接待中。(华山所)</p>\n', '2018-11-07 10:15:00', '2018-11-07 17:00:00', 6.75, 5, '拆迁安置', '五华分局', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-11-08 11:20:53', '2018-11-21 16:20:04', NULL, '村民', 'qkfy', NULL, NULL, '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 110, NULL, 2392, 2507, 'cz', '城镇', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', 'cscq', '城市拆迁', 'sryx', '10人以下', '2', NULL, NULL, 0.00, 0, 0, 'yb', NULL, NULL, 'e63b2f01-6892-47ed-a489-621e64f60cb1', 'JS', NULL, 'Y', 110, 151951, 'zzwd', '530102000000', '李敏', '吴文', NULL, '吴文', 5186, 'ZQYSB', 'N', NULL);
