INSERT INTO `qb_zdasjqbxx` VALUES (5197, NULL, NULL, NULL, NULL, 'sf', '维稳信息', NULL, '2018-11-07 10:30:38', '11月7日在宜良县做工的农民工6人因工程款被拖欠到省政府上访', '<p>11月7日10时许，6名农民工到省政府上访，反映，他们2014年11月与西南交通建设集团有限公司签订劳务合作协议，承包宜良县耿家营、古城片区双龙公路路面硬化处理工程。2015年8月工程完工，验收合格。但至今西南交建拖欠上访人工程款300余万元，由于讨要未果所以到省政府上访，要求尽快拿到工程款。现信访接待中。（华山所）</p>\n', '2018-11-07 10:15:00', '2018-11-07 17:00:00', 6.75, 6, '合同纠纷 ', '五华分局', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-11-08 14:37:43', '2018-11-21 16:11:58', NULL, '农民工', 'qkfy', NULL, NULL, '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 110, NULL, 2392, 2519, 'nn', '农村', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', 'lzjf', '劳资纠纷', 'sryx', '10人以下', '2', NULL, NULL, 0.00, 0, 0, 'yb', NULL, NULL, '345eb5a5-55fd-4d31-bbcb-3b2f46efbb58', 'JS', NULL, 'Y', 110, 151951, 'zzwd', '530102000000', '李敏', '吴文', NULL, '吴文', 5196, 'ZQYSB', 'N', NULL);
