INSERT INTO `qb_zdasjqbxx` VALUES (5247, NULL, NULL, NULL, NULL, 'sf', '维稳信息', NULL, '2018-11-09 11:31:27', '西山区庄稼塘村村民5人因失地问题到省政府上访', '<p>9日08:30分许，5名西山区福海街道办事处庄稼塘村村民到省政府上访，反映，2009年该村绝大部分土地被当地政府征用建盖&ldquo;湖畔之梦&rdquo;小区等商业区，村民也搬迁进了回迁房，来访村民称他们失地后没有生活来源，生活困难，征地补偿款至今没有完全发放到位，引发600余户村民不满。多次向政府有关部门反映要求解决此问题未果所以到省政府上访，要求政府解决失地村民的生活补助，另外投诉该村村支书贪污征地补偿款，村内账目混乱等问题。省信访局接待后告知其回昆明市反映。08:45分许，5人全部离开省政府。 华山东路派出所 2018年11月9日</p>\n', '2018-11-09 08:30:00', '2018-11-09 08:45:00', 0.25, 5, '拆迁安置', '五华分局', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-11-12 14:07:58', '2018-11-12 14:16:38', NULL, '村民', 'qkfy', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 110, NULL, 2392, 2507, 'nn', '农村', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', 'zd', '征地', 'sryx', '10人以下', '1', NULL, NULL, 0.00, 0, 0, 'yb', NULL, NULL, 'e3d8bcf4-3533-4e1e-a203-6727be905e40', 'JS', NULL, 'Y', 110, 151951, 'zzwd', '530102000000', '李敏', '吴文', NULL, '吴文', 5246, 'ZQYSB', 'N', NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (5256, NULL, NULL, NULL, NULL, 'jj', '维稳信息', NULL, '2018-11-12 13:51:28', '11月12日20余名东风云南汽车厂退休职工例行到厂门口聚集', '<p>11月12日09时许，20余名东风云南汽车厂的退休职工例行到厂大门口聚集，要求东风云南汽车厂解决他们的问题及诉求。 在下厂区大门口侧边栏杆处、门上分别横挂6幅白底黑字布标，内容为&ldquo;要求公开土地置换合同、严查国有土地流失和国有资产流失&rdquo;。&ldquo;严惩贪污腐败，我们要吃饭、我们要生存、还我血汗钱。&rdquo; &ldquo;云汽土地置换项目必须由真正的云汽职工代表全程参与！ &rdquo; &ldquo;强烈要求胡伟民回云汽，给当年被强制买断员工一个说法！ &rdquo;。 至10时27分许聚集职工自行收拾标语离开，无过激行为，未堵门堵路。相关情况持续关注中。（黑林铺所）</p>\n', '2018-11-12 09:00:00', '2018-11-12 10:27:00', 1.45, 20, '企业改制/破产 ', '五华分局', '东风云南汽车厂', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-11-13 10:21:50', '2018-11-13 10:29:38', NULL, NULL, 'qkfy', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 110, NULL, 2606, NULL, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', NULL, NULL, 0.00, 0, 0, 'yb', NULL, NULL, 'cd241126-e644-4bf2-867a-73dfe50efab8', 'JS', NULL, 'Y', 110, 151951, 'gayw', '530102000000', '秦怀国', '吴文', NULL, '吴文', 5255, 'ZQYSB', 'N', NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (5258, NULL, NULL, NULL, NULL, 'sf', '维稳信息', NULL, '2018-11-12 16:02:02', '11月12日8名群众因投资资金受损到省政府上访', '<p>12日14:40分许，8名群众到省政府上访，反映，他们从2016年8月起，投资一个总公司位于蒙自市的&ldquo;红河州汇丰投资管理总公司&rdquo;的产品，该公司在蒙自市、开远市、石林县、宜良县、安宁市五个地区开设分公司，吸收民间融资进行房地产项目开发，来访人称五地大约有2000余人投资该公司，投资金额9200余万元，公司按照15%-20%的利率每月返回给投资人。今年6月份起。5家公司突然关停，投资人的本金及返利均无法拿到，引发投资人恐慌，遂向各地公安部门反映情况，来访人称他们了解到目前有安宁市和宜良县公安机关受理了此案，但其余3个地方的公安部门没有受理，因多次向当地公安部门反映要求立案未得到答复，所以到省政府上访，要求上级政府督促当地公安机关尽快立案。省信访局接待后告知其向公安厅反映诉求。15:20分许，8人离开省政府。(华山所)</p>\n', '2018-11-12 14:40:00', '2018-11-12 15:20:00', 0.67, 8, '合同纠纷 ', '五华分局', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-11-13 10:21:50', '2018-11-13 10:39:19', NULL, NULL, 'qkfy', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 110, NULL, 2392, NULL, 'cz', '城镇', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'tsqt', '特殊群体', 'jzwt', '集资问题', 'sryx', '10人以下', '1', NULL, NULL, 0.00, 0, 0, 'yb', NULL, NULL, '2b1eb7d3-f546-4f71-b7fd-2ae9a5b7ed24', 'JS', NULL, 'Y', 110, 151951, 'zzwd', '530102000000', '李敏', '吴文', NULL, '吴文', 5257, 'ZQYSB', 'N', NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (5260, NULL, NULL, NULL, NULL, 'sf', '维稳信息', NULL, '2018-11-12 17:12:36', '我省民办教师11月12日到省教育厅上访现场情况', '<p>11月12日上午9时许，以曲靖、昭通、红河为主的我省40余名民办、代课教师到省教育厅上访。INSERT INTO `qb_zdasjqbxx` VALUES (5262, NULL, NULL, NULL, NULL, 'sf', '维稳信息', NULL, '2018-11-12 17:13:07', '11月12日“中天城.天阔”楼盘业主到五华区政府上访', '<p>据工作掌握，2018年11月12日9时，80余名&ldquo;中天城.天阔&rdquo;楼盘业主拟于到五华区政府上访。主要诉求：在春节前要通正电、煤气、基础设施完善等诉求，并要求见区长。9时30分许，区信访局、区住建局、开发商接待了上访业主代表。 12日15时许接待完毕，代表向上访业主传达了接访的情况，称在2018年12月以前，政府会协调开发商完善水电等基础设施的问题，15时15分许，上访人员陆续离开区政府。在此期间，上访人员未出现打布、标喊口号等过激行为。INSERT INTO `qb_zdasjqbxx` VALUES (5281, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'bse', '2018-11-13 11:02:49', '11月13日普洱市南屏镇村民8人因搬迁补偿问题到省政府上访', NULL, '2018-11-13 10:00:00', '2018-11-13 17:00:00', 7.00, 8, '身份待遇', '五华分局', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-11-14 10:05:22', '2018-11-21 15:58:34', NULL, '村民', 'qkfy', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 110, NULL, 2392, 2507, 'nn', '农村', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', 'snwt', '三农问题', 'sryx', '10人以下', '2', NULL, NULL, 0.00, 0, 0, 'yb', NULL, NULL, '051100bc-d68f-4fda-b2be-c7576ac677c6', 'JS', NULL, 'Y', 110, 151951, 'zzwd', '530102000000', '李敏', '吴文', NULL, '吴文', 5280, 'ZQYSB', 'N', NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (5283, NULL, NULL, NULL, NULL, 'sf', '维稳信息', NULL, '2018-11-13 11:04:35', '11月13日禄劝籍大中专毕业生8人因分配问题到省政府上访', '<p>11月13日10:20分许，8名生源地是昆明市禄劝县的2001年-2003年毕业的大中专毕业生到省政府上访，主要反映他们入学时与学校签订了分配协议，但是毕业时学校没有按照招生简章和协议的约定给他们分配工作，涉及禄劝籍学生40余人，故到省政府上访，要求解决工作分配问题。现8人正在等待接待。(华山所)</p>\n', '2018-11-13 10:20:00', '2018-11-13 17:00:00', 6.67, 8, '合同纠纷 ', '五华分局', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-11-14 10:05:22', '2018-11-21 16:02:22', NULL, '大中专毕业生', 'qkfy', NULL, NULL, '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 110, NULL, 2392, 2542, 'nn', '农村', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', 'dzzbm', '对政策不满', 'sryx', '10人以下', '2', NULL, NULL, 0.00, 0, 0, 'yb', NULL, NULL, '1e063376-4518-4866-a3e2-2fc012b8ede6', 'JS', NULL, 'Y', 110, 151951, 'zzwd', '530102000000', '李敏', '吴文', NULL, '吴文', 5282, 'ZQYSB', 'N', NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (5285, NULL, NULL, NULL, NULL, 'sf', '维稳信息', NULL, '2018-11-13 10:57:36', '寻甸县金源乡村民5人因建房问题到省政府上访', '<p>13日09:40分许，5名昆明市寻甸县金源乡龙潭村委会村民到省政府上访，反映他们5户是建档立卡贫困户，根据国家相关政策，他们应该享受建房方面（如优先建房，建筑面积多10%等）的各项优惠，但是村委会成员将这些待遇给了他们的亲属，导致他们5户困难户利益受损，一年来他们多次向有关部门反映要求享受贫困户的建房待遇未果所以到省政府上访，投诉村委会滥用职权，任人唯亲的违纪行为，要求尽快解决他们5户困难户的建房问题。该起村民于11月7日曾就相同诉求到访过，由于没有得到解决今日再次到访。省信访局接待后告知其回昆明市反映。10:20分许，5人全部离开省政府。（华山所）</p>\n', '2018-11-13 09:40:00', '2018-11-13 10:20:00', 0.67, 5, '身份待遇', '五华分局', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-11-14 10:05:22', '2018-11-21 15:11:46', NULL, NULL, 'qkfy', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 110, NULL, 2392, NULL, 'nn', '农村', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', 'snwt', '三农问题', 'sryx', '10人以下', '1', NULL, NULL, 0.00, 0, 0, 'yb', NULL, NULL, 'b72759d2-159d-44d4-b495-a7f2e30a0bc5', 'JS', NULL, 'Y', 110, 151951, 'zzwd', '530102000000', '李敏', '吴文', NULL, '吴文', 5284, 'ZQYSB', 'N', NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (5287, NULL, NULL, NULL, NULL, 'sf', '维稳信息', NULL, '2018-11-13 09:24:56', '我市部分参战老兵11月12日到市住房保障局反映诉求现场情况', '<p>11月12日上午9时许，高金海、肖文忠、李旭、林志等8名我市对越参战老兵到位于西昌路的&ldquo;昆明市住房保障局&rdquo;反映诉求。老兵主要反映减免居住市管公租房参战老兵的租金问题。市住房保障局相关领导对老兵进行了答复，表示就该问题目前正整理材料向上级政府部门上报，希望涉及的老兵耐心等待。 至上午11时左右，上述老兵离开市住房保障局，反映问题过程中未出现过激行为。相关情况进一步收集掌握中。(国保大队)</p>\n', '2018-11-12 09:00:00', '2018-11-12 11:00:00', 2.00, 8, '特殊群体', '五华分局', '昆明市住房保障局', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-11-14 10:05:22', '2018-11-14 10:20:31', NULL, NULL, 'qkfy', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 110, NULL, NULL, NULL, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', NULL, NULL, 0.00, 0, 0, 'yb', NULL, NULL, 'a33d619f-0744-4a7e-a32f-474b7357cccf', 'JS', NULL, 'Y', 110, 151951, 'gayw', '530102000000', '余咏', '吴文', NULL, '吴文', 5286, 'ZQYSB', 'N', NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (5289, NULL, NULL, NULL, NULL, 'jj', '维稳信息', NULL, '2018-11-13 13:53:42', '20余名东风云南汽车厂退休职工聚集在滇缅大道东风云南汽车厂门口', '<p>11月13日09时12分许，20余名东风云南汽车厂的退休职工分散聚集在云南汽车厂上下厂区大门两侧人行道上及厂区大门口，并陆续有职工到此聚集，要求东风云南汽车厂解决他们的问题及诉求，在下厂区大门口侧边栏杆处、门上分别横挂6幅白底黑字布标，内容为&ldquo;要求公开土地置换合同、严查国有土地流失和国有资产流失&rdquo;。&ldquo;严惩贪污腐败，我们要吃饭、我们要生存、还我血汗钱。&rdquo; &ldquo;云汽土地置换项目必须由真正的云汽职工代表全程参与！ &rdquo; &ldquo;强烈要求胡伟民回云汽，给当年被强制买断员工一个说法！ &rdquo;。至10时23分许聚集职工自行收拾标语离开，无过激行为，未堵门堵路。相关情况持续关注中。</p>\n', '2018-11-13 09:12:00', '2018-11-13 10:23:00', 1.18, 20, '企业改制/破产 ', '五华分局', '东风云南汽车厂', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-11-14 10:05:22', '2018-11-14 10:33:57', NULL, NULL, 'qkfy', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 110, NULL, 2606, NULL, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', NULL, NULL, 0.00, 0, 0, 'yb', NULL, NULL, 'e7720df7-7a9d-4af3-b6e3-4a68fd4923b8', 'JS', NULL, 'Y', 110, 151951, 'gayw', '530102000000', '秦怀国', '吴文', NULL, '吴文', 5288, 'ZQYSB', 'N', NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (5291, NULL, NULL, NULL, NULL, 'sf', '维稳信息', NULL, '2018-11-13 15:23:50', '西山区老螺狮湾商铺业主29人因回迁问题到省政府上访', '<p>13日14:20分许，29名西山区老螺狮湾商铺业主到省政府上访，反映，2009年老螺狮湾商铺拆迁时，他们没有迁到新螺狮湾，而是要求原址回迁，多年来他们一直领取过渡费自行择业，今年8月份，他们了解到拆迁原址处建好了&ldquo;大悦城商业中心&rdquo;，后多次向西山区政府反映要求尽快原址回迁，由于一直没有得到答复所以到省政府上访，要求尽快回迁（具体涉及户数来访人不清楚），另外来访人还反映政府拖欠他们2016年至今两年的过渡费没有发放，要求尽快发放。省信访局接待后告知其回西山区政府反映诉求。14:55分许，29人全部离开省政府。</p>\n', '2018-11-13 14:20:00', '2018-11-13 14:55:00', 0.58, 29, '移民搬迁', '五华分局', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-11-14 10:05:22', '2018-11-14 10:37:04', NULL, NULL, 'qkfy', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 110, NULL, 2392, NULL, 'cz', '城镇', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'gtgsyz', '个体工商业者', 'cscq', '城市拆迁', 'sldws', '16-50人', '1', NULL, NULL, 0.00, 0, 0, 'yb', NULL, NULL, '1179878e-f15d-41ef-947f-1314337adbf1', 'JS', NULL, 'Y', 110, 151951, 'zzwd', '530102000000', '李敏', '吴文', NULL, '吴文', 5290, 'ZQYSB', 'N', NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (5295, NULL, NULL, NULL, NULL, 'jj', '维稳信息', NULL, '2018-11-14 17:31:31', '11月14日东风云南汽车厂退休职工发生规模性聚集', '<p>11月14日9时许，东风云南汽车厂有两个群体聚集，一是90余名买断工龄职工到厂区三楼会议室聚集，汽车厂厂方领导与职工进行交流协商，职工主要诉求是要求汽车厂厂方纠正2010年改制过程中的错误做法，解决2010年买断工龄的职工的生活困境，补足工龄买断赔偿金的差额部分,涉及人员约300人左右。黑林所民警全程在现场维护秩序，至14时30分许，双方协商无果，聚集职工陆续散去，整个过程无过激行为发生。二是20余名退休职工仍按惯例在厂区大门两侧聚集，挂6幅白底黑字布标，内容为&ldquo;要求公开土地置换合同、严查国有土地流失和国有资产流失&rdquo;。&ldquo;严惩贪污腐败，我们要吃饭、我们要生存、还我血汗钱。&rdquo;、&ldquo;云汽土地置换项目必须由真正的云汽职工代表全程参与！ &rdquo;、&ldquo;强烈要求胡伟民回云汽，给当年被强制买断员工一个说法！ &rdquo;。10时30分许，聚集职工自行收拾标语离开。</p>\n', '2018-11-14 09:00:00', '2018-11-14 10:30:00', 1.50, 90, '企业改制/破产 ', '五华分局', '东风云南汽车厂', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-11-15 10:14:13', '2018-11-15 10:21:30', NULL, NULL, 'qkfy', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 110, NULL, 2606, NULL, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', NULL, NULL, 0.00, 0, 0, 'yb', NULL, NULL, '667e81b3-6c99-495e-927c-84016cc683ab', 'JS', NULL, 'Y', 110, 151951, 'gayw', '530102000000', '栗斌', '吴文', NULL, '吴文', 5294, 'ZQYSB', 'N', NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (5297, NULL, NULL, NULL, NULL, 'sf', '维稳信息', NULL, '2018-11-14 17:36:35', '多地民办教师9人因生活补助问题到省政府上访', '<p>14日16:45分许，9名来自昭通、曲靖、昆明的民办教师到省政府上访，主要反映，针对他们民办教师群体，当地政府按照每年教龄850元一次性给予他们补偿，后予以辞退，他们认为医保养老保险也应该由政府承担，故多次向当地政府反映，要求解决医保养老等问题，由于一直未得到解决，所以到省政府上访，要求：1、被强制辞退的民代幼教师要给予离岗退养金；2、未签辞退协议未领取辞退费的要求返岗或转岗使用；3、按照教龄解决相应的医保养老等保障，省信访局接待后告知其回各地教育部门反映咨询。17:00许，9人离开接访大厅，目前聚集在省政府大门人行道处。华山所密切关注中。</p>\n', '2018-11-14 16:45:00', '2018-11-14 17:00:00', 0.25, 9, '身份待遇', '五华分局', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-11-15 10:14:13', '2018-11-15 10:33:52', NULL, NULL, 'qkfy', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 110, NULL, 2392, NULL, 'nn', '农村', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm,jsxs', '农民,教师学生', 'gzfldy', '工资福利待遇', 'sryx', '10人以下', '2', NULL, NULL, 0.00, 0, 0, 'yb', NULL, NULL, '86e2dbad-450f-41d5-8d20-9f30f2b668b4', 'JS', NULL, 'Y', 110, 151951, 'zzwd', '530102000000', '李敏', '吴文', NULL, '吴文', 5296, 'ZQYSB', 'N', NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (5299, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'bse', '2018-11-14 17:38:10', '    11月14日10余民办教师到教育厅上访', '<p>11月14日上午10时许，来自昭通、会泽、宣威、红河等地的民办教师10余人聚集在云南省昆明市小菜园立交桥下学府路2号云南省教育厅门外上访，要求解决工资待遇等问题。上访人员情绪稳定，全程无过激行为， 17时上访人员许全数撤离，我所将持续关注，如有情况将及时上报。 (莲华所)</p>\n', '2018-11-14 10:00:00', '2018-11-14 17:00:00', 7.00, 10, '身份待遇', '五华分局', '云南省教育厅', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-11-15 10:14:13', '2018-11-15 10:37:13', NULL, NULL, 'qkfy', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 110, NULL, 2634, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'qy', '请愿', NULL, NULL, NULL, NULL, 'sryx', '10人以下', '1', NULL, NULL, 0.00, 0, 0, 'yb', NULL, NULL, '3e11f3b0-cdb7-4878-a57a-a599f6cdc295', 'JS', NULL, 'Y', 110, 151951, 'zzwd', '530102000000', '贾锐', '吴文', NULL, '吴文', 5298, 'ZQYSB', 'N', NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (5301, NULL, NULL, NULL, NULL, 'sf', '维稳信息', NULL, '2018-11-15 15:08:55', '罗平县阿岗镇村民8人因占地补偿到省政府上访', '<p>15日14:05分许，曲靖市罗平县阿岗镇高桥村委会村民到省政府上访，反映，2016年初，当地政府因为修建水库的需要，在库区淹没了部分土地，其中包括高桥村21户村民的17亩土地，村民称政府的占地补偿一直没有发给村民，引发村民不满，多次向当地政府反映要求尽快发放未果所以到省政府上访，要求尽快拿到占地补偿，另外，村民还投诉该村村主任在丈量淹没土地的过程中不公平，每户村民的被淹土地面积均少量，导致村民的补偿费减少，利益受损，要求公平丈量土地。现曲靖市信访局工作人员正在接待来访村民。</p>\n', '2018-11-15 14:05:00', '2018-11-15 17:00:00', 2.92, 8, '损害赔偿 ', '五华分局', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-11-16 14:43:44', '2018-11-21 16:32:18', NULL, NULL, 'qkfy', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 110, NULL, 2392, NULL, 'nn', '农村', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', 'snwt', '三农问题', 'sryx', '10人以下', '2', NULL, NULL, 0.00, 0, 0, 'yb', NULL, NULL, 'a03d1b48-305b-4ddf-8bec-81d73ae9a3c0', 'JS', NULL, 'Y', 110, 151951, 'zzwd', '530102000000', '李敏', '吴文', NULL, '吴文', 5300, 'ZQYSB', 'N', NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (5303, NULL, NULL, NULL, NULL, 'jj', '维稳信息', NULL, '2018-11-15 15:21:09', '90余名金座集资人员到市体育馆上访', '<p>11月15日9时10分许，90余名金座人员到市体育馆进行例行上访。市工商局、市金融办相关人员在现场进行接访，分局民警正在现场开展秩序维护疏导化解等工作。上访人员秩序良好，无过激行为。 12时15分许，全部上访金座人员自行离开市体育馆。</p>\n', '2018-11-15 09:10:00', '2018-11-15 12:15:00', 3.08, 100, '特殊群体', '五华分局', NULL, NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-11-16 14:43:44', '2018-11-21 15:54:52', NULL, NULL, 'qkfy', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 110, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'sf', '上访', NULL, NULL, NULL, NULL, 'yblyr', '101-200人', '2', NULL, NULL, 0.00, 0, 0, 'yb', NULL, NULL, '54cf031c-48b4-4e8c-8535-62e9112a77b3', 'JS', NULL, 'Y', 110, 151951, 'zzwd', '530102000000', '马翚', '吴文', NULL, '吴文', 5302, 'ZQYSB', 'N', NULL);
