INSERT INTO `qb_zdasjqbxx` VALUES (5323, NULL, NULL, NULL, NULL, 'sf', '维稳信息', NULL, '2018-11-16 11:15:35', '11月16日西山区“文化空间”商铺业主及租户15人因合同纠纷到省政府上访', '<p>&nbsp;&nbsp;&nbsp;&nbsp;16日9:20分许，15名西山区&ldquo;文化空间&rdquo;商铺业主及租户到省政府上访，反映，2009年，西山区新闻路老毛纺厂地块进行拆迁改造，开发商是云南中天文化产业股份有限公司，在原址重建商住楼，来访人称，拆迁时该公司要求原租户先交商铺预定金之后才能优先选铺面，近900余人向该公司交了预付款，但是，公司负责人又以每平米5-7万元不等的价格将商铺卖给其他人，存在一间铺面既租又卖的违法现象，现文化城还剩10%的工程没有完工，来访人认为是开发商恶意烂尾，至今没有交付业主，引发业主及租户两方面群体的不满，部分人曾到西山区公安分局及西山区政府反映要求彻底调查该公司的违法行为和交房等问题，由于一直没有得到答复所以到省政府上访，业主要求尽快将验收合格的商铺交给他们，租户要求开发商将预付款以及9年的利息退换给他们。现信访接待中。</p>\n\n<p>&nbsp;</p>\n\n<p>&nbsp;</p>\n\n<p>华山东路派出所</p>\n\n<p>2018年11云16日</p>\n', '2018-11-16 09:20:00', '2018-11-16 17:00:00', 7.67, 15, '合同纠纷 ', '五华分局', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-11-19 14:08:42', '2018-11-21 16:33:04', NULL, NULL, 'qkfy', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 110, NULL, 2392, NULL, 'cz', '城镇', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'gtgsyz', '个体工商业者', 'cscq', '城市拆迁', 'syr', '11-15人', '2', NULL, NULL, 0.00, 0, 0, 'yb', NULL, NULL, '314c8dc6-72f4-469d-a1df-2abc4029a5f8', 'JS', NULL, 'Y', 110, 151951, 'zzwd', '530102000000', '李敏', '吴文', NULL, '吴文', 5322, 'ZQYSB', 'N', NULL);
