INSERT INTO `qb_zdasjqbxx` VALUES (5455, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'bs', '2018-12-04 13:46:22', '12月4日约100名洪家营村民继续围堵黄土坡强林加油站', '<p>12月4日9时许，约100名高新区万裕社区洪家营居民小组村民（均是60、70岁的老年人）聚集在本市五华区黄土坡立交桥旁的强林加油站，并使用凳子围坐在该加油站进出口，导致该加油站车辆无法进出，据村民称，若此事件得不到解决，将继续围堵加油站。虹山所社区民警在现场维持秩序，暂无纠纷冲突。持续关注中。（虹山所）</p>\n', '2018-12-04 09:00:00', '2018-12-04 17:00:00', 8.00, 90, '土地租金问题', '虹山派出所', '黄土坡立交桥旁的强林加油站', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-12-06 09:54:34', '2018-12-14 14:20:34', NULL, '村民', 'qkfy', NULL, NULL, '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 110, NULL, NULL, 2507, NULL, NULL, 'gzfy', '工作反映', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', NULL, NULL, 0.00, 0, 0, 'yb', NULL, NULL, 'cedf6763-0fc0-463d-9318-7335c11c4f36', 'JS', NULL, 'Y', 110, 151951, 'gayw', '530102410000', '周永春', '吴文', NULL, '吴文', 5454, 'ZQYSB', 'N', NULL);
