INSERT INTO `qb_zdasjqbxx` VALUES (5481, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'bs', '2018-12-06 13:52:37', '棕树营片区70余名拆迁户约到五华区政府上访', '<p>12月6日9时许，棕树营片区70余名拆迁户到五华区政府上访，要求尽快发放2018年下半年的拆迁过渡安置费，并在一环内或拆迁地块周边安置回迁房。经接待，上述人员于12时许自行离开。现不排除棕树营片区拆迁户不满答复，继续到省、市政府上访的可能。相关情况密切关注中。</p>\n', '2018-12-06 14:48:00', '2018-12-06 12:00:00', 2.80, 70, '拆迁安置', '虹山派出所', '五华区政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-12-10 14:45:22', '2018-12-14 14:25:26', NULL, '棕树营', 'qkfy', NULL, NULL, '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 110, NULL, 2393, 2509, 'dzjg', '党政机关', 'badys', '不安定因素', NULL, NULL, 'sf', '上访', 'nm', '农民', 'cscq', '城市拆迁', 'wsyr', '51-100人', '2', NULL, NULL, 0.00, 0, 0, 'yb', NULL, NULL, '65f2e973-0ba3-402d-a61c-bfdce22f6fdb', 'JS', NULL, 'Y', 110, 151951, 'zzwd', '530102410000', '耿红玲', '吴文', NULL, '吴文', 5480, 'ZQYSB', 'N', NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (5483, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'bse', '2018-12-06 15:11:16', '寻甸县六哨乡村民6人因征地补偿问题到省政府上访', '<p>6日14:30分许，6名昆明市寻甸县六哨乡马鞍山村委会村民到省政府上访，反映，2007年寻甸县开展&ldquo;引板入清&rdquo;工程，该工程系引水入昆工程的一部分，征用了该村6户村民的自留山地200余亩，按照当时的补偿标准，该6户村民应该得到征地补偿150余万元，由于一直没有领到补偿款，6户村民一直向政府相关部门反映要求尽快拿到补偿款，后经当地政府协调，制定了分配方案，但6户认为该方案不公平，损害了他们的利益，且补偿款拖欠时间过长，土地增值，要求政府重新按照现在的补偿标准公平公正的分配给他们。因在当地反映未得到满意答复所以到省政府上访，要求提高征地补偿标准，制定合理的分配方案。省信访局接待后告知其向昆明市政府反映。15:00许，6人全部离开省政府。</p>\n', '2018-12-06 14:30:00', '2018-12-06 15:00:00', 0.50, 6, '征地赔偿', '华山东路派出所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-12-10 14:45:22', '2018-12-19 11:17:28', NULL, '村民', 'qkfy', NULL, NULL, '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 110, NULL, 2392, 2507, 'nn', '农村', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', 'zd', '征地', 'sryx', '10人以下', '1', NULL, NULL, 0.00, 0, 0, 'yb', NULL, NULL, '78000a03-f0d5-48d0-a762-ac2bd37d40c4', 'JS', NULL, 'Y', 110, 151951, 'zzwd', '530102450000', '李敏', '吴文', NULL, '吴文', 5482, 'ZQYSB', 'N', NULL);
