INSERT INTO `qb_zdasjqbxx` VALUES (5505, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'bse', '2018-12-10 13:24:28', '12月10日国资商贸城供货商28人因货款被拖欠到省政府上访', '<p>10日10:40分许，28名群众到省政府上访，反映，他们均是云南省国有资本运营商城管理有限公司（该公司位于环湖东路上）的供货商，从2017年下半年起，国资商城开始出现货款延迟支付的情况，至2017年底至今，共拖欠41家供货商货款1700余万元，因多次与商贸城协商无果，所以到省政府上访，要求上级政府出面协调尽快发放货款。省信访局接待后告知其向省国资委反映。由于不满接待结果，28人仍然滞留在接访大厅，密切关注中。</p>\n', '2018-12-10 10:40:00', '2018-12-10 17:00:00', 6.33, 28, '拖欠货款', '华山东路派出所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-12-14 09:54:32', '2018-12-19 11:04:34', NULL, '商业问题', 'qkfy', NULL, NULL, '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 110, NULL, 2392, 2546, 'cz', '城镇', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'gtgsyz', '个体工商业者', 'mjjf', '民间纠纷', 'sldws', '16-50人', '2', NULL, NULL, 0.00, 0, 0, 'yb', NULL, NULL, '70c96a24-5a91-4030-9b39-2cad0b5d1da9', 'JS', NULL, 'Y', 110, 151951, 'zzwd', '530102450000', '李敏', '吴文', NULL, '吴文', 5504, 'ZQYSB', 'N', NULL);
