INSERT INTO `qb_zdasjqbxx` VALUES (5561, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'bse', '2018-12-14 13:38:39', '砚山县阿舍乡村民16人因征地问题到省政府上访', '<p>14日11:25分许，16名文山州砚山县阿舍乡阿舍村委会阿舍一组村民到省政府上访，反映，2000年3月，当地乡政府开展阿舍新寨小组异地搬迁工作，搬迁地址包含了阿舍一组27户村民的100亩承包土地，同时当地政府要求该27户村民不准在征地上栽种，要在征地上建房给搬迁户居住，征地费每亩12000元。此政策引发27户村民不满，他们认为此征地行为不符合国家政策，不同意被征用，因为向当地政府反映未得到解决所以到省政府上访，要求撤销征地文件，同时举报当地政府粗暴征地的违纪行为。省信访局接待后告知其向文山州反映。11:40分许，来访群众全部离开省政府。</p>\n', '2018-12-14 11:25:00', '2018-12-14 11:40:00', 0.25, 16, '征地赔偿', '华山东路派出所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-12-17 13:44:06', '2018-12-19 09:50:18', NULL, '村民', 'qkfy', NULL, NULL, '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 110, NULL, 2392, 2507, 'nn', '农村', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', 'snwt', '三农问题', 'sldws', '16-50人', '1', NULL, NULL, 0.00, 0, 0, 'yb', NULL, NULL, '14868def-4481-462c-9812-63ddcc971a1a', 'JS', NULL, 'Y', 110, 151951, 'zzwd', '530102450000', '李敏', '吴文', NULL, '吴文', 5560, 'ZQYSB', 'N', NULL);
