INSERT INTO `qb_zdasjqbxx` VALUES (5627, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'bse', '2018-12-19 12:43:42', '12月19日20余名东风云南汽车厂退休职工例行在厂门口聚集', '<p>12月19日9时6分，20余名东风云南汽车厂的退休职工分散聚集在云南汽车厂上下厂区大门两侧人行道上及厂区大门口，要求东风云南汽车厂解决他们的问题及诉求，在下厂区大门口侧边栏杆处、门上分别横挂6幅白底黑字布标，内容为&ldquo;要求公开土地置换合同、严查国有土地流失和国有资产流失&rdquo;。&ldquo;严惩贪污腐败，我们要吃饭、我们要生存、还我血汗钱。&rdquo;至10时14分聚集职工自行收拾标语离开，未堵门堵路，无发生过激行为。相关情况持续关注中。（黑林铺所）</p>\n', '2018-12-19 09:06:00', '2018-12-19 10:14:00', 1.13, 20, '企业改制/破产 ', '五华分局', '东风云南汽车厂', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-12-20 09:38:13', '2018-12-20 11:21:11', NULL, '企业改制职工', 'qkfy', NULL, NULL, '拉横幅', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 110, NULL, 2606, 2513, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', NULL, NULL, 0.00, 0, 0, 'yb', NULL, NULL, '99ab7f90-b5ce-43b5-9981-2c96c755844b', 'JS', NULL, 'Y', 110, 151951, 'gayw', '530102000000', '秦怀国', '吴文', NULL, '吴文', 5626, 'ZQYSB', 'Y', NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (5629, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'bse', '2018-12-19 16:32:54', '20余名职工继18日后再次聚集在小康大道省冶金集团前反映集资建房问题', '<p>12月19日14时30时许，20余名职工继18日后再次聚集在小康大道省冶金集团前。 据初步了解，2008年共有135户冶金集团职工交纳过1万元集资款参与集资建房，但因当时政策调整，不允许建盖集资房。冶金集团于2014年在昆明市西山区电缆厂建盖了职工住房，职工住房性质变为廉租房，而当时交纳集资款的职工又不符合租住廉租房的条件，导致该部分员工未能分配到住房。冶金集团表示，可以按照国家活期储蓄的方式给予退款，但职工代表表示不同意。经核实，现已有15户办理了退款。 相关情况进一步掌握中。</p>\n', '2018-12-19 14:30:00', '2018-12-19 17:00:00', 2.50, 20, '逾期未交房', '五华分局', '小康大道省冶金集团', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-12-20 09:38:13', '2018-12-24 14:43:29', NULL, '企业职工', 'qkfy', NULL, NULL, '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 110, NULL, NULL, 2538, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', NULL, NULL, 0.00, 0, 0, 'yb', NULL, NULL, '32f1050c-c5ac-4703-996a-ab493b57ddb9', 'JS', NULL, 'Y', 110, 151951, 'gayw', '530102000000', '彭永军', '吴文', NULL, '吴文', 5628, 'ZQYSB', 'Y', NULL);
