INSERT INTO `qb_zdasjqbxx` VALUES (5758, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'bse', '2018-12-21 14:44:07', '五华分局迅速处置黄土坡旧货市场商户聚集警情', '<p>12月21日早10时许，五华区丰宁办事处综合执法中队到二环西路黄土坡旧货市场开展市场关停、拆迁工作。12时48分，10余名队员乘两辆执法电瓶车到二环西路黄土坡旧货市场门口告知商户职能出，不能进市场，立即引发市场商户聚集并与综合执法中队人员对峙。12时58分许，聚集的商户及围观人员人数升至百余人，后30余名商户将其中一辆电动车推到二环西路上，使二环西路非机动车道无法通行，引发二环西路黄土坡段由东向西方向单向交通不畅。13时01分，五华分局指挥中心接虹山所警力报称在黄土坡旧货市场城管执法受阻，车辆被推到路上。 接报情况后，五华分局指挥中心第一时间调派巡特警警力到先期到场支援虹山所，分局党委书记、纪委副书记杨卫平立即带指挥中心、法制大队、特警大队等部门警力赶到现场开展工作。在现场，一是开展法制宣传工作，将聚集人员劝离公路；二是开展劝解稳控工作，防止聚集升级为打砸等事态发生；三是在民警的妥善控制下，现场两辆综合执法中队电动车妥善驶离；四是开展调查取证工作，收集违法情况。截止上报时，现场公路交通已恢复。虹山所已通报丰宁办事处，相关部门人员陆续到现场开展稳控劝导工作。现场双方均有人声称受伤，均已自行前往医治，虹山所现场取证、调查工作有序开展中。区政法委书记霍延、副区长涂力军及相关部门人员在现场指挥处置善后工作，事态密切关注中。</p>\n', '2018-12-21 10:00:00', '2018-12-21 15:00:00', 5.00, 90, '商户职能', '虹山派出所', '黄土坡旧货市场', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 152082, '012865', '2018-12-24 14:49:48', '2018-12-25 16:37:39', NULL, '农贸市场租户', 'qkfy', NULL, NULL, '堵路', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 250, NULL, NULL, 2548, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', NULL, NULL, 0.00, 0, 0, 'yb', NULL, NULL, '32fcf17a-ab3d-450a-96ab-770c00dcbf72', 'JS', 5678, 'Y', 250, 152082, 'gayw', '530102410000', '王国伟', '吴文', 5685, '吴文', 5677, 'SJFF', 'N', NULL);
