INSERT INTO `qb_zdasjqbxx` VALUES (5804, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'bse', '2018-12-24 11:08:45', '12月24日6名昭通昭阳区外嫁妇女因土地问题到省政府上访', '<p>12月24日10时35分许，6名昭通市昭阳区旧圃镇各乡的外嫁女到省政府上访，反映嫁到外地后原村分配的土地被收回，嫁入地一直未分配土地，导致&ldquo;两头无地&rdquo;，没有生活来源，涉及的昭阳区外嫁女共800余人，多年来她们一直向政府有关部门反映要求解决土地问题无果。省信访局接待后告知其回昭通市反映。11时许，6人离开省政府。 华山东路派出所 2018年12月24日</p>\n', '2018-12-24 10:35:00', '2018-12-25 11:00:00', 24.42, 6, '山林土地问题 ', '华山东路派出所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151986, '016597', '2018-12-25 09:38:13', '2019-01-14 17:41:10', NULL, '群众', 'qkfy', NULL, NULL, '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 210, NULL, 2392, 2555, 'nn', '农村', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', 'snwt', '三农问题', 'sryx', '10人以下', '1', NULL, NULL, 0.00, 0, 0, 'yb', NULL, NULL, '5a8d0091-8e6f-41ca-a396-ef7b2c6a0c48', 'JS', 5710, 'Y', 210, 151986, 'zzwd', '530102450000', '李敏', '李利宏', 5803, '李利宏', 5709, 'SJZL', 'N', NULL);
