INSERT INTO `qb_zdasjqbxx` VALUES (5822, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'ws', '2018-12-21 11:42:22', '12月21日6名“12.13”人员到省政府上访', '<p>21日10时30分许，6名来自昆明、上海、陕西的泛亚有色金属投资人到省政府上访，主要诉求是：找泛亚有色金属协调小组及其负责人，询问此事的进展情况，追讨受损的投资资金。省信访局接待后告知其，此事属涉法涉诉范围，应到省市金融办咨询。11时许，6人全部离开省政府。（华山所）</p>\n', '2018-12-21 10:30:00', '2018-12-21 11:00:00', 0.50, 6, '特殊群体', '华山东路派出所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 152054, '013420', '2018-12-24 14:49:48', '2019-01-15 17:44:27', NULL, '金融受损群体', 'qkfy', NULL, NULL, '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 290, NULL, 2392, 2500, 'cz', '城镇', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'tsqt', '特殊群体', 'jzwt', '集资问题', 'sryx', '10人以下', '1', NULL, NULL, 0.00, 0, 0, 'yb', NULL, NULL, 'c42c0192-2405-4374-8a15-bac0f03d9a83', 'JS', 5674, 'Y', 290, 152054, 'zzwd', '530102450000', '李敏', '李利宏', 5683, '李利宏', 5673, 'SJFF', 'N', NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (5823, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'bse', '2018-12-20 14:51:02', '在富源煤矿打工的河南籍农民工9人因工钱被拖欠到省政府上访', '<p>20日10：30分许，9名河南籍农民工到省政府上访，反映，今年9月起，他们经人介绍来到曲靖市富源县黄泥河镇一私人煤矿打工，由于煤矿处在转型过程中，工人的工作时断时续，后9人不愿继续打工，要求结算工钱回原籍，因为在工钱数额、工伤补助等方面与老板存在争议，一直拿不到工钱，所以到省政府上访，要求尽快解决争议，拿到工钱。曲靖市信访局负责人接待9人后告知其回富源县解决，由于对接待结果不满，9人一直滞留信访局，11：30分许，9人离开信访局一直在人行道中段处聚集，14：10分许，9人携带行李再次来到省信访局，现一直在武警操场处聚集，目前秩序平稳。我所密切关注中。 华山东路派出所 2018年12月20日</p>\n', '2018-12-20 10:30:00', '2018-12-20 14:10:00', 3.67, 9, '讨薪索款', '华山东路派出所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 152054, '013420', '2018-12-24 14:34:22', '2019-01-15 17:44:27', NULL, '农民工', 'qkfy', NULL, NULL, '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 290, NULL, 2392, 2519, 'nn', '农村', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', 'mjjf', '民间纠纷', 'sryx', '10人以下', '2', NULL, NULL, 0.00, 0, 0, 'yb', NULL, NULL, 'a82b511a-ede4-4d34-b15b-e5f7a03aba9a', 'JS', 5661, 'Y', 290, 152054, 'zzwd', '530102450000', '李敏', '吴文', 5718, '吴文', 5660, 'SJZL', 'N', NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (5824, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'bse', '2018-12-19 09:23:20', '19日嵩阳镇小庄子村村民8人因拆迁安置问题到省政府上访', '<p>19日08:35分许，8名嵩明县嵩阳镇小庄子村村民到省政府上访，反映，2010年当地政府因建设园博园工程的需要，拆迁了工程范围内的村民，来访群众称，他们23户村民的住房于2010年6月份被拆后至今政府没有给他们安置，过渡费发放到2016年10月后就停止了发放，村民了解到，政府的安置房今年已建好，但街道办一直没有通知他们23户领房，引发村民不满，多次向当地政府反映要求尽快安置未果，所以到省政府上访，要求尽快入住安置房，被拖欠的过渡费也要尽快补发。省信访局接待后告知其回昆明市反映。09:00许，8人全部离开省政府。</p>\n', '2018-12-19 08:35:00', '2018-12-19 09:00:00', 0.42, 8, '拆迁安置', '五华分局', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 152054, '013420', '2018-12-20 09:38:13', '2019-01-15 17:44:28', NULL, '村民', 'qkfy', NULL, NULL, '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 290, NULL, 2392, 2507, 'nn', '农村', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', 'zd', '征地', 'sryx', '10人以下', '1', NULL, NULL, 0.00, 0, 0, 'yb', NULL, NULL, 'f1977f07-af9e-414c-8e6a-c56d0e328cab', 'JS', 5623, 'Y', 290, 152054, 'zzwd', '530102000000', '李敏', '吴文', 5635, '吴文', 5622, 'SJFF', 'N', NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (5825, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'bse', '2018-12-20 15:52:57', '元谋县江边乡村民6人因移民安置问题到省政府上访', '<p>20日14:50分许，6名楚雄州元谋县江边乡龙街村委会村民到省政府上访，反映，由于修建金沙江乌东德水电站的需要，当地政府目前正在进行移民搬迁工作，来访群众称，当地政府没有向群众宣传有关移民搬迁的补偿、安置等政策就强制村民签订搬迁协议，引发村民不满，多次向当地政府反映要求公开移民政策未果所以到省政府上访，要求：必须有村民代表参与有关移民搬迁办法的制定并公开所有安置政策。省信访局接待后告知其向楚雄州反映。15：10分许，6人全部离开省政府。</p>\n', '2018-12-20 14:50:00', '2018-12-20 15:10:00', 0.33, 6, '拆迁安置', '华山东路派出所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 152054, '013420', '2018-12-24 14:34:22', '2019-01-15 17:44:28', NULL, '村民', 'qkfy', NULL, NULL, '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 290, NULL, 2392, 2507, 'nn', '农村', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', 'ymwt', '移民问题', 'sryx', '10人以下', '1', NULL, NULL, 0.00, 0, 0, 'yb', NULL, NULL, 'bb2b22d3-fcae-41d1-8e02-d0457a6c0f1d', 'JS', 5657, 'Y', 290, 152054, 'zzwd', '530102450000', '李敏', '吴文', 5668, '吴文', 5656, 'SJFF', 'N', NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (5826, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'bse', '2018-12-24 09:57:29', '原云南交通机械厂退休职工6人因工资待遇问题到省政府上访', '<p>24日09:20分许，6名云南博业工程机械股份有限公司退休职工到省政府上访，反映，该单位原叫云南交通机械厂，由省交通厅管辖，后交由昆明市交运局管辖，2008年该单位企业改制，成为民营企业，近几年由于企业经营困难无力发放退休职工的工资，也没有为他们缴纳社保医保等，导致职工无法就医，达到退休年龄的职工也不能办理退休手续，涉及20余人，引发职工不满故到省政府上访，要求解决上述问题。省信访局接待后告知其向昆明市政府反映。09:45分许，6人全部离开省政府。</p>\n', '2018-12-24 09:20:00', '2018-12-24 09:45:00', 0.42, 6, '企业改制/破产 ', '华山东路派出所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 152054, '013420', '2018-12-25 09:38:13', '2019-01-15 17:44:28', NULL, '企业改制职工', 'qkfy', NULL, NULL, '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 290, NULL, 2392, 2513, 'cz', '城镇', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'ltxry', '离退休人员', 'gzfldy', '工资福利待遇', 'sryx', '10人以下', '1', NULL, NULL, 0.00, 0, 0, 'yb', NULL, NULL, 'b08384eb-f2f0-452a-9418-3c25212e944f', 'JS', 5706, 'Y', 290, 152054, 'zzwd', '530102450000', '李敏', '吴文', 5712, '吴文', 5705, 'SJFF', 'N', NULL);
