INSERT INTO `qb_zdasjqbxx` VALUES (5829, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'bse', '2018-12-24 10:58:33', '12月24日全省各地大中专毕业生11人因毕业安置问题到省政府上访', '<p>12月24日9时35分许，11名全省各地大中专生因毕业安置问题到省政府上访，来访人员来自红河、昭通、玉溪，现10余人聚集在武警操场处，称还有人员陆续到来，相关情况进一步收集中。（华山所）</p>\n', '2018-12-24 09:35:00', '2018-12-24 12:00:00', 2.42, 11, '毕业安置', '华山东路派出所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 152054, '013420', '2018-12-25 09:38:13', '2019-01-15 17:44:28', NULL, '大中专毕业生', 'qkfy', NULL, NULL, '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 290, NULL, 2392, 2542, 'nn', '农村', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'tsqt', '特殊群体', 'dzzbm', '对政策不满', 'syr', '11-15人', '2', NULL, NULL, 0.00, 0, 0, 'yb', NULL, NULL, '1463eac1-3f84-4997-83c4-4360bc9abc43', 'JS', 5708, 'Y', 290, 152054, 'zzwd', '530102450000', '李敏', '吴文', 5727, '吴文', 5707, 'SJZL', 'N', NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (5830, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'bse', '2018-12-24 17:49:56', '师大附中美华国际高中学生家长80余人因学校停课到省政府上访', '<p>24日17时10分许，80余名云南师大附中美华国际高中（学校地址在盘龙区青云街道白沙河水库南侧）的学生、家长及教职工到省政府上访，反映，上周六，家长收到教师短信，称学校连续三个月未给教职工发放工资，教职工要罢课，引发学生及家长强烈不满，遂到盘龙区教育局等有关部门反映要求学校立即恢复教学 ，由于得不到满意答复，故到省政府上访，要求：1、政府相关部门尽快解决学生入学问题；2、要求所有美华国际高中学生 全部由云南师大附中接收，并解决学籍问题。该群众来到省政府后由于省信访局下班不再接待群众，80余人现聚集在省政府大门侧面，要求见分管教育的副省长。分局已安排警力在现场开展稳控工作。</p>\n', '2018-12-24 17:10:00', '2018-12-25 18:45:00', 25.58, 80, '教师工资问题罢课', '华山东路派出所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 152054, '013420', '2018-12-25 09:37:25', '2019-01-15 17:44:28', NULL, '学生家长', 'qkfy', NULL, NULL, '无', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'PoliticalStability', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 290, NULL, 2392, 2544, 'cz', '城镇', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'tsqt', '特殊群体', 'mjjf', '民间纠纷', 'wsyr', '51-100人', '2', NULL, NULL, 0.00, 0, 0, 'yb', NULL, NULL, '08a756ef-9c7f-498a-9241-b3ee3663515e', 'JS', 5698, 'Y', 290, 152054, 'zzwd', '530102450000', '李敏', '吴文', 5730, '吴文', 5697, 'SJZL', 'N', NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (5831, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'bse', '2018-12-24 17:53:55', '“北京路天主教堂复建项目”20余名商铺业主聚集华山东路天主教堂', '<p>12月24日下午15时许，&ldquo;北京路天主教堂复建项目&rdquo;20余名商铺业主聚集华山东路天主教堂，要求&ldquo;天主教爱国会&rdquo;补偿商户因项目停工导致的投资损失。现聚集的商铺业主已由天主教爱国会和民宗局相关负责人引导至社区居委会会议室进行商谈，投资户有序提问，教会副主教岳天德正进行解释工作，现场秩序稳定。 研判分析：&ldquo;北京路天主教堂复建项目&rdquo;商户在&ldquo;平安夜&rdquo;当天到天主教爱国会进行围堵及讨要说法的行为已对华山东路天主堂&ldquo;平安夜&rdquo;活动构成影响，甚至可能引发商户与当晚参加弥撒活动的信众发生冲突及对峙事件，如事态进一步恶化将给我市造成恶劣影响。 建议及工作措施： 1、建议区民宗局上报市民宗部门对该教堂&ldquo;平安夜、圣诞节&rdquo;活动予以取消； 2、分局将密切关注收集现场动态情况，并及时上报，同时会同辖区派出所加强该教堂内外的秩序维护及巡逻防范，做好聚集商户疏导劝解工作。 &nbsp; 相关情况收集掌握中。</p>\n', '2018-12-24 15:00:00', '2018-12-24 17:00:00', 2.00, 20, '商户停工损失', '华山东路派出所', '华山东路天主教', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 152054, '013420', '2018-12-25 09:37:26', '2019-01-15 17:44:28', NULL, '商业问题', 'qkfy', NULL, NULL, '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 290, NULL, NULL, 2546, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', NULL, NULL, 0.00, 0, 0, 'yb', NULL, NULL, 'daadc10a-448e-42b9-a90d-700a6f2dbf1e', 'JS', 5702, 'Y', 290, 152054, 'gayw', '530102450000', '余咏', '吴文', 5731, '吴文', 5701, 'SJZL', 'N', NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (5832, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'bse', '2018-12-20 14:51:02', '在富源煤矿打工的河南籍农民工9人因工钱被拖欠到省政府上访', '<p>20日10：30分许，9名河南籍农民工到省政府上访，反映，今年9月起，他们经人介绍来到曲靖市富源县黄泥河镇一私人煤矿打工，由于煤矿处在转型过程中，工人的工作时断时续，后9人不愿继续打工，要求结算工钱回原籍，因为在工钱数额、工伤补助等方面与老板存在争议，一直拿不到工钱，所以到省政府上访，要求尽快解决争议，拿到工钱。曲靖市信访局负责人接待9人后告知其回富源县解决，由于对接待结果不满，9人一直滞留信访局，11：30分许，9人离开信访局一直在人行道中段处聚集，14：10分许，9人携带行李再次来到省信访局，现一直在武警操场处聚集，目前秩序平稳。我所密切关注中。 华山东路派出所 2018年12月20日</p>\n', '2018-12-20 10:30:00', '2018-12-20 14:10:00', 3.67, 9, '讨薪索款', '华山东路派出所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 152054, '013420', '2018-12-24 14:34:22', '2019-01-15 17:44:29', NULL, '农民工', 'qkfy', NULL, NULL, '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 290, NULL, 2392, 2519, 'nn', '农村', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', 'mjjf', '民间纠纷', 'sryx', '10人以下', '2', NULL, NULL, 0.00, 0, 0, 'yb', NULL, NULL, 'a82b511a-ede4-4d34-b15b-e5f7a03aba9a', 'JS', 5661, 'Y', 290, 152054, 'zzwd', '530102450000', '李敏', '吴文', 5666, '吴文', 5660, 'SJFF', 'N', NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (5833, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'ws', '2018-12-21 11:42:22', '12月21日6名“12.13”人员到省政府上访', '<p>21日10时30分许，6名来自昆明、上海、陕西的泛亚有色金属投资人到省政府上访，主要诉求是：找泛亚有色金属协调小组及其负责人，询问此事的进展情况，追讨受损的投资资金。省信访局接待后告知其，此事属涉法涉诉范围，应到省市金融办咨询。11时许，6人全部离开省政府。（华山所）</p>\n', '2018-12-21 10:30:00', '2018-12-21 11:00:00', 0.50, 6, '特殊群体', '华山东路派出所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 152054, '013420', '2018-12-24 14:49:48', '2019-01-15 17:44:29', NULL, '金融受损群体', 'qkfy', NULL, NULL, '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 290, NULL, 2392, 2500, 'cz', '城镇', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'tsqt', '特殊群体', 'jzwt', '集资问题', 'sryx', '10人以下', '1', NULL, NULL, 0.00, 0, 0, 'yb', NULL, NULL, 'c42c0192-2405-4374-8a15-bac0f03d9a83', 'JS', 5674, 'Y', 290, 152054, 'zzwd', '530102450000', '李敏', '李利宏', 5723, '李利宏', 5673, 'SJZL', 'N', NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (5834, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'bse', '2018-12-24 11:08:45', '12月24日6名昭通昭阳区外嫁妇女因土地问题到省政府上访', '<p>12月24日10时35分许，6名昭通市昭阳区旧圃镇各乡的外嫁女到省政府上访，反映嫁到外地后原村分配的土地被收回，嫁入地一直未分配土地，导致&ldquo;两头无地&rdquo;，没有生活来源，涉及的昭阳区外嫁女共800余人，多年来她们一直向政府有关部门反映要求解决土地问题无果。省信访局接待后告知其回昭通市反映。11时许，6人离开省政府。 华山东路派出所 2018年12月24日</p>\n', '2018-12-24 10:35:00', '2018-12-25 11:00:00', 24.42, 6, '山林土地问题 ', '华山东路派出所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 152054, '013420', '2018-12-25 09:38:13', '2019-01-15 17:44:29', NULL, '群众', 'qkfy', NULL, NULL, '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 290, NULL, 2392, 2555, 'nn', '农村', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', 'snwt', '三农问题', 'sryx', '10人以下', '1', NULL, NULL, 0.00, 0, 0, 'yb', NULL, NULL, '5a8d0091-8e6f-41ca-a396-ef7b2c6a0c48', 'JS', 5710, 'Y', 290, 152054, 'zzwd', '530102450000', '李敏', '李利宏', 5728, '李利宏', 5709, 'SJZL', 'N', NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (5836, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2017-08-23 15:38:20', '23日四川麻省科技有限公司投资户10人到省政府上访', '8月23日9时10分，10名群众到省政府信访局上访。主要反映：2005年四川麻省科技有限公司使用非法手段进行融资，套取他们资金上百万元，法院判决后他们的资金一直无法收回的问题。现到省政府信访局上访要求省政府帮助解决落实此事。信访局接访答复后，上述人员于10时30分自行离去。相关情况在进一步掌握中。\r\n', '2017-08-23 09:10:00', '2017-08-23 10:30:00', 1.33, 10, '涉众型经济案件', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 152054, '013420', '2017-08-28 10:51:02', '2019-01-15 17:44:29', NULL, '个体户', NULL, '0', '1', NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, 290, NULL, 2392, 2547, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', 'eb429d5b-20f6-40db-b500-02a620d78e71', 'JS', 172, 'Y', 290, 152054, NULL, '530102450000', NULL, NULL, 5820, NULL, 5835, 'SJZL', 'N', NULL);
