INSERT INTO `qb_zdasjqbxx` VALUES (5858, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'bse', '2018-12-24 17:49:56', '师大附中美华国际高中学生家长80余人因学校停课到省政府上访', '<p>24日17时10分许，80余名云南师大附中美华国际高中（学校地址在盘龙区青云街道白沙河水库南侧）的学生、家长及教职工到省政府上访，反映，上周六，家长收到教师短信，称学校连续三个月未给教职工发放工资，教职工要罢课，引发学生及家长强烈不满，遂到盘龙区教育局等有关部门反映要求学校立即恢复教学 ，由于得不到满意答复，故到省政府上访，要求：1、政府相关部门尽快解决学生入学问题；2、要求所有美华国际高中学生 全部由云南师大附中接收，并解决学籍问题。该群众来到省政府后由于省信访局下班不再接待群众，80余人现聚集在省政府大门侧面，要求见分管教育的副省长。分局已安排警力在现场开展稳控工作。</p>\n', '2018-12-24 17:10:00', '2018-12-25 18:45:00', 25.58, 80, '教师工资问题罢课', '华山东路派出所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 152054, '013420', '2018-12-25 09:37:25', '2019-01-18 14:57:52', NULL, '学生家长', 'qkfy', NULL, NULL, '无', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'PoliticalStability', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 290, NULL, 2392, 2544, 'cz', '城镇', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'tsqt', '特殊群体', 'mjjf', '民间纠纷', 'wsyr', '51-100人', '2', NULL, NULL, 0.00, 0, 0, 'yb', NULL, NULL, '08a756ef-9c7f-498a-9241-b3ee3663515e', 'JS', 5698, 'Y', 290, 152054, 'zzwd', '530102450000', '李敏', '吴文', 5857, '吴文', 5697, 'SJZL', 'N', NULL);
