INSERT INTO `qb_zdasjqbxx` VALUES (6441, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'bs', '2018-12-21 14:22:59', '12月21日70余名和谐世纪、书香门第、华龙人家业主到红云街道办事处聚集维权', '<p>&nbsp;</p>\n\n<p>12月21日9时，70余名和谐世纪、书香门第、华龙人家小区业主因与物管公司龙泉物业就成立业主委员会存在纠纷，到红云街道办聚集，要求成立业主委员会，希望由街道办出面协调处理。按照程序成立业主委员会需要小区内30%业主签字同意，目前维权业主组织人数尚达不到30%。经红云街道办事处安排人员接待后，维权业主于12时全部离开。（红云所）</p>\n', '2018-12-21 09:00:00', '2018-12-21 12:00:00', 3.00, 70, '产权物业', '红云派出所', '红云街道办', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-12-24 14:49:48', '2019-09-02 14:15:35', NULL, '业主购房者', 'qkfy', NULL, NULL, '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 110, NULL, NULL, 2528, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', NULL, NULL, 0.00, 0, 0, 'yb', NULL, NULL, '52c12f5d-8802-4163-8fae-233793580df2', 'JS', 5676, 'Y', 340, 152106, 'gayw', '530102490000', '牛显权', '吴文', 6440, '吴文', 5675, 'SJZL', 'N', NULL);
